import React, { Component } from "react";
import { Link, withRouter } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { registerUser, logoutUser } from "../../actions/authAutentications";
import classnames from "classnames";
import InputGroup from "react-bootstrap/InputGroup";
import Button from "react-bootstrap/Button";
import { registerPrenotazione } from "../../actions/prenotazioniActions";

//componenti da react-bootstrap
import { Container, Form } from "react-bootstrap";
import { Checkbox } from "@material-ui/core";

//componenti da material-ui

class NuovaPrenotazione extends Component {
  constructor() {
    super();
    this.state = {
      partenza: "",
      data_partenza: "",
      ora_partenza: "",
      destinazione: "",
      data_arrivo: "",
      ora_arrivo: "",
      ora_partenza: "",
      parcheggio_partenza: "",
      parcheggio_destinazione: "",
      idVeicolo: "",
      idCliente: "",
      idAutista: "",
      tipo_veicolo: "",
      type: "switch",
      presenza_autista: false,
      errors: {},
    };

    this.onChange = this.onChange.bind(this);
    this.setType = this.setType.bind(this);
  }

  setType() {
    // così dovrebbe funzionare
    if (this.state.data_partenza === "2021-08-08") {
      this.state.errors.check =
        "Non puoi selezionare l'autista se la data è 3 giorni";
      this.state.type = "hidden";
    }
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors,
      });
    }
  }
  onChange = (e) => {
    this.setState({
      [e.target.id]: e.target.value,
    });

    if (e.target.id == "tipo_veicolo" && e.target.value == "Autovettura") {
      document.getElementById("presenza-autista").disabled = false;
    }
    if (e.target.id == "tipo_veicolo" && e.target.value != "Autovettura") {
      document.getElementById("presenza-autista").disabled = true;
      if (document.getElementById("presenza-autista").hasAttribute("checked")) {
        document.getElementById("presenza-autista").removeAttribute("checked");
      }
    }
    e.preventDefault();
  };

  onSubmit = (e) => {
    e.preventDefault();

    var today = new Date();
    var ora = today.getHours();
    var minuti = today.getMinutes();
    var dd = today.getDate();
    var mm = today.getMonth() + 1; //January is 0!
    var yyyy = today.getFullYear();
    if (dd < 10) {
      dd = "0" + dd;
    }
    if (mm < 10) {
      mm = "0" + mm;
    }
    if (ora < 10) {
      ora = "0" + ora;
    }
    if (minuti < 10) {
      minuti = "0" + minuti;
    }

    today = yyyy + "-" + mm + "-" + dd;
    var orario = ora + ":" + minuti;

    /*if (e.target.id=="data_partenza" && this.state.data_partenza===today){
      document.getElementById('ora_partenza').min=orario;
    }*/

    const newPrenotazione = {
      ora_partenza: this.state.ora_partenza,
      partenza: this.state.partenza,
      ora_arrivo: this.state.ora_arrivo,
      ora_partenza: this.state.ora_partenza,
      data_partenza: this.state.data_partenza,
      data_arrivo: this.state.data_arrivo,
      destinazione: this.state.destinazione,
      parcheggio_partenza: this.state.parcheggio_partenza,
      parcheggio_destinazione: this.state.parcheggio_destinazione,
      tipo_veicolo: this.state.tipo_veicolo,
      idVeicolo: this.state.idVeicolo,
      idCliente: this.state.idCliente,
      idAutista: this.state.idAutista,
      presenza_autista: this.state.presenza_autista,
    };

    if (this.state.data_partenza === today) {
      if (this.state.ora_partenza < orario)
        this.state.errors.ora_partenza = "Errore partenza";
    }
    if (this.state.data_arrivo === today) {
      if (this.state.ora_arrivo < orario)
        this.state.errors.ora_arrivo = "Errore arrivo";
    }

    //this.props.registerPrenotazione(newPrenotazione, this.props.history);
  };

  render() {
    const { errors } = this.state;
    var today = new Date();
    var dd = today.getDate();
    var mm = today.getMonth() + 1; //January is 0!
    var yyyy = today.getFullYear();
    if (dd < 10) {
      dd = "0" + dd;
    }
    if (mm < 10) {
      mm = "0" + mm;
    }

    today = yyyy + "-" + mm + "-" + dd;

    return (
      <Container sm style={{ marginTop: "5rem", marginBottom: "5rem" }}>
        <div className="contenitore nuovaPrenotazione">
          <h1
            style={{
              textAlign: "center",
              color: "black",
              gridColumn: "2",
              gridRow: "2",
            }}
          >
            Nuova Prenotazione
          </h1>

          <div className="Form">
            <div className="form-group">
              <input
                onChange={this.onChange}
                value={this.state.partenza}
                error={errors.partenza}
                id="partenza"
                type="text"
                placeholder="Inserisci l'indirizzo di partenza"
                required
                className={classnames("form-control", {
                  invalid: errors.partenza,
                })}
              />
              <span className="red-text">{errors.partenza}</span>
            </div>
            <div className="form-group">
              <label>Data di partenza</label>
              <input
                onChange={async (e) => {
                  await this.onChange(e);
                  await this.setType();
                }}
                value={this.state.data_partenza}
                error={errors.data_partenza}
                id="data_partenza"
                type="date"
                min={today}
                required
                className={classnames("form-control", {
                  invalid: errors.data_partenza,
                })}
              />
              <span className="red-text">{errors.data_partenza}</span>
            </div>
            <div className="form-group">
              <label>Orario di partenza</label>
              <input
                onChange={this.onChange}
                value={this.state.ora_partenza}
                error={errors.ora_partenza}
                id="ora_partenza"
                type="time"
                required
                className={classnames("form-control", {
                  invalid: errors.ora_partenza,
                })}
              />
              <span className="red-text">{errors.ora_partenza}</span>
            </div>
            <div className="form-group">
              <input
                onChange={this.onChange}
                value={this.state.destinazione}
                error={errors.destinazione}
                id="destinazione"
                type="text"
                placeholder="Inserisci l'indirizzo di destinazione"
                required
                className={classnames("form-control", {
                  invalid: errors.destinazione,
                })}
              />
              <span className="red-text">{errors.destinazione}</span>
            </div>
            <div className="form-group">
              <label>Data di arrivo</label>
              <input
                onChange={async (e) => {
                  await this.onChange(e);
                  await this.setType();
                }}
                value={this.state.data_arrivo}
                error={errors.data_arrivo}
                id="data_arrivo"
                type="date"
                min={today}
                required
                className={classnames("form-control", {
                  invalid: errors.data_arrivo,
                })}
              />
              <span className="red-text">{errors.data_arrivo}</span>
            </div>
            <div className="form-group">
              <label>Orario di arrivo</label>
              <input
                onChange={this.onChange}
                value={this.state.ora_arrivo}
                error={errors.ora_arrivo}
                id="ora_arrivo"
                type="time"
                required
                className={classnames("form-control", {
                  invalid: errors.ora_arrivo,
                })}
              />
              <span className="red-text">{errors.ora_arrivo}</span>
            </div>

            <label>Tipo Veicolo</label>
            <select
              onChange={this.onChange}
              value={this.state.tipo_veicolo}
              error={errors.tipo_veicolo}
              id="tipo_veicolo"
              type="text"
              className={classnames("form-control", {
                invalid: errors.tipo_veicolo,
              })}
              required
            >
              <option disabled selected>
                Tipo Veicolo
              </option>
              <option>Autovettura</option>
              <option>Moto</option>
              <option>Bicicletta</option>
              <option>Monopattino</option>
            </select>

            <Form.Check
              type="switch"
              id="custom-switch"
              label="Dichiaro di possedere un dispositivo portatile per l'intera durata della corsa"
              required
              style={{ margin: "5px" }}
            />

            <Form.Check
              type={this.state.type}
              id="presenza-autista"
              label="Voglio un Autista per la mia corsa"
              style={{ margin: "5px" }}
            />
            <span className="red-text">{errors.check}</span>
            <Button variant="primary" type="submit">
              Avanti
            </Button>
          </div>
        </div>
      </Container>
    );
  }
}

NuovaPrenotazione.propTypes = {
  logoutUser: PropTypes.func.isRequired,
  registerPrenotazione: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps, { registerPrenotazione })(
  NuovaPrenotazione
);
