import React from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import {
  loginUser,
  setCurrentUser,
  logoutUser,
} from "../../../actions/authAutentications";

import { Link, withRouter } from "react-router-dom";

//componenti da react-bootstrap
import { Navbar, ButtonGroup, DropdownButton, Dropdown } from "react-bootstrap";

//icone da material ui-icons
import PersonIcon from "@material-ui/icons/Person";
import { blue } from "@material-ui/core/colors";
import NotificationsActiveIcon from "@material-ui/icons/NotificationsActive";
import IconButton from "@material-ui/core/IconButton";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import MoreVertIcon from "@material-ui/icons/MoreVert";

//Componenti nostre
import Logo from "../../../immagini/logo.png";

const options = [
  "None",
  "Atria",
  "Callisto",
  "Dione",
  "Ganymede",
  "Hangouts Call",
  "Luna",
  "Oberon",
  "Phobos",
  "Pyxis",
  "Sedna",
  "Titania",
  "Triton",
  "Umbriel",
];

let stile;
const ITEM_HEIGHT = 48;
const [anchorEl, setAnchorEl] = this.useState(null);
const open = Boolean(anchorEl);

class MyNavbar extends React.Component {
  onLogoutClick = (e) => {
    e.preventDefault();
    this.props.logoutUser();
  };
  constructor(props) {
    super(props);
  }

  handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  handleClose = () => {
    setAnchorEl(null);
  };

  render() {
    try {
      const { user } = this.props.auth;
      if (user.ruolo === "Admin" || user.ruolo === "Addetto") stile = "AA";
      else if (user.ruolo === "Cliente" || user.ruolo === "Autista")
        stile = "CA";
    } catch (error) {
      stile = "default";
    }
    switch (stile) {
      case "AA":
        return (
          <div>
            <Navbar className="Navbar justify-content-between">
              <Navbar.Brand href="/HomeCliente">
                <img
                  src={Logo}
                  width="50"
                  height="50"
                  alt="Karm-Logo"
                  className="logonavbar"
                />
              </Navbar.Brand>
              <Navbar.Toggle />
              <DropdownButton
                menuAlign="right"
                title="Profilo"
                id="dropdown-menu-align-right"
                style={{ backgroundColor: "#010101" }}
              >
                <Dropdown.Item eventKey="1" href="/IlMioProfilo">
                  Il Mio Profilo
                </Dropdown.Item>
                <Dropdown.Divider />
                <Dropdown.Item
                  eventKey="4"
                  style={{ backgroundColor: "red" }}
                  onClick={this.onLogoutClick}
                >
                  Disconnettiti
                </Dropdown.Item>
              </DropdownButton>
            </Navbar>
          </div>
        );
      case "CA":
        return (
          <div>
            <Navbar className="Navbar justify-content-between">
              <Navbar.Brand href="/HomeCliente">
                <img
                  src={Logo}
                  width="50"
                  height="50"
                  alt="Karm-Logo"
                  className="logonavbar"
                />
              </Navbar.Brand>
              <Navbar.Toggle />
              <Navbar.Collapse className="justify-content-end">
                <div>
                  <IconButton
                    aria-label="more"
                    aria-controls="long-menu"
                    aria-haspopup="true"
                    onClick={handleClick}
                  >
                    <MoreVertIcon />
                  </IconButton>
                  <Menu
                    id="long-menu"
                    anchorEl={this.anchorEl}
                    keepMounted
                    open={open}
                    onClose={this.handleClose}
                    PaperProps={{
                      style: {
                        maxHeight: ITEM_HEIGHT * 4.5,
                        width: "20ch",
                      },
                    }}
                  >
                    {options.map((option) => (
                      <MenuItem
                        key={option}
                        selected={option === "Pyxis"}
                        onClick={this.handleClose}
                      >
                        {option}
                      </MenuItem>
                    ))}
                  </Menu>
                </div>
                <DropdownButton
                  menuAlign="right"
                  title="Profilo"
                  id="dropdown-menu-align-right"
                >
                  <Dropdown.Item eventKey="1" href="/IlMioProfilo">
                    Il Mio Profilo
                  </Dropdown.Item>
                  <Dropdown.Divider />
                  <Dropdown.Item
                    eventKey="4"
                    style={{ backgroundColor: "red" }}
                    onClick={this.onLogoutClick}
                  >
                    Disconnettiti
                  </Dropdown.Item>
                </DropdownButton>
              </Navbar.Collapse>
            </Navbar>
          </div>
        );
      default:
        return (
          <div>
            <Navbar
              className="Navbar"
              style={{ display: "block", textAlign: "center" }}
            >
              <Navbar.Brand href="/Login">
                <img
                  src={Logo}
                  width="50"
                  height="auto"
                  alt="Karm-Logo"
                  className="logonavbar1"
                />
              </Navbar.Brand>
            </Navbar>
          </div>
        );
    }
  }
}

MyNavbar.propTypes = {
  logoutUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps, { logoutUser })(MyNavbar);
