import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutUser, setCurrentUser } from "../actions/authAutentications";
import Button from "react-bootstrap/Button";
import LockOpenIcon from "@material-ui/icons/LockOpen";
import LockIcon from "@material-ui/icons/Lock";
import BuildIcon from "@material-ui/icons/Build";
import "../App.css";

class GestioneCorsa extends Component {
  constructor(props) {
    super(props);
  }

  onLogoutClick = (e) => {
    e.preventDefault();
    this.props.logoutUser();
  };

  render() {
    const { user } = this.props.auth;

    if (user.ruolo === "Admin" || user.ruolo === "Addetto") {
      return (
        <div
          className="contenitore GestioneCorsa"
          style={{ backgroundColor: "black" }}
        >
          <div
            style={{
              gridColumn: "2/ span 2",
              gridRow: "2",
              display: "flex",
              justifySelf: "start",
              margin: "20px",
            }}
          >
            <h1 style={{ justifySelf: "center" }}>
              Gestisci la tua corsa con KARM!
            </h1>
          </div>
          <div
            style={{
              gridColumn: "2",
              gridRow: "3",
              display: "flex",
              justifySelf: "center",
              margin: "20px",
            }}
          >
            <Button
              href="/"
              className="bottone"
              variant="outline-primary"
              style={{ borderRadius: "400px" }}
            >
              Consegna
            </Button>
          </div>
          <div
            style={{
              gridColumn: "2",
              gridRow: "4",
              display: "flex",
              justifySelf: "center",
              margin: "20px",
            }}
          >
            <Button
              href="/"
              className="bottone"
              variant="outline-primary"
              style={{ borderRadius: "400px" }}
            >
              Rilascio
            </Button>
          </div>
          <div style={{ gridColumn: "3", gridRow: "3" }}>
            <div style={{ display: "flex" }}>
              <LockOpenIcon
                style={{ height: "40px", width: "40px", color: "white" }}
              >
                {" "}
              </LockOpenIcon>
              <h5 style={{ color: "white", alignSelf: "end" }}>
                {" "}
                Prendi il tuo veicolo!
              </h5>
            </div>
            <h6 style={{ marginLeft: "55px" }}> Mettiti in corsa con KARM</h6>
          </div>
          <div style={{ gridColumn: "3", gridRow: "4" }}>
            <div style={{ display: "flex" }}>
              <LockIcon
                style={{ height: "40px", width: "40px", color: "white" }}
              >
                {" "}
              </LockIcon>
              <h5 style={{ color: "white", alignSelf: "end" }}>
                {" "}
                Rilascia il tuo veicolo!
              </h5>
            </div>
            <h6 style={{ marginLeft: "55px" }}> Termina la tua corsa</h6>
          </div>
          <div style={{ gridColumn: "1", gridRow: "6" }}>
            <Button
              href="javascript:history.back()"
              className="indietro"
              variant="primary"
            >
              INDIETRO
            </Button>
          </div>
        </div>
      );
    } else if (user.ruolo === "Cliente" || user.ruolo === "Autista") {
      return (
        <div
          className="contenitore GestioneCorsa"
          style={{ backgroundColor: "black" }}
        >
          <div
            style={{
              gridColumn: "2/ span 2",
              gridRow: "2",
              display: "flex",
              justifySelf: "start",
              margin: "20px",
            }}
          >
            <h1>Gestisci la tua corsa con KARM!</h1>
          </div>
          <div
            style={{
              gridColumn: "2",
              gridRow: "5",
              display: "flex",
              justifySelf: "center",
              margin: "20px",
            }}
          >
            <Button
              href="/"
              className="bottone"
              variant="outline-primary"
              style={{ borderRadius: "400px" }}
            >
              Comunica Guasto
            </Button>
          </div>
          <div
            style={{
              gridColumn: "2",
              gridRow: "3",
              display: "flex",
              justifySelf: "center",
              margin: "20px",
            }}
          >
            <Button
              href="/"
              className="bottone"
              variant="outline-primary"
              style={{ borderRadius: "400px" }}
            >
              Consegna
            </Button>
          </div>
          <div
            style={{
              gridColumn: "2",
              gridRow: "4",
              display: "flex",
              justifySelf: "center",
              margin: "20px",
            }}
          >
            <Button
              href="/"
              className="bottone"
              variant="outline-primary"
              style={{ borderRadius: "400px" }}
            >
              Rilascio
            </Button>
          </div>
          <div style={{ gridColumn: "3", gridRow: "3" }}>
            <div style={{ display: "flex" }}>
              <LockOpenIcon
                style={{ height: "40px", width: "40px", color: "white" }}
              >
                {" "}
              </LockOpenIcon>
              <h5 style={{ color: "white", alignSelf: "end" }}>
                {" "}
                Prendi il tuo veicolo!
              </h5>
            </div>
            <h6 style={{ marginLeft: "55px" }}> Mettiti in corsa con KARM</h6>
          </div>
          <div style={{ gridColumn: "3", gridRow: "4" }}>
            <div style={{ display: "flex" }}>
              <LockIcon
                style={{ height: "40px", width: "40px", color: "white" }}
              >
                {" "}
              </LockIcon>
              <h5 style={{ color: "white", alignSelf: "end" }}>
                {" "}
                Rilascia il tuo veicolo!
              </h5>
            </div>
            <h6 style={{ marginLeft: "55px" }}> Termina la tua corsa</h6>
          </div>
          <div style={{ gridColumn: "3", gridRow: "5" }}>
            <div style={{ display: "flex" }}>
              <BuildIcon
                style={{ height: "40px", width: "40px", color: "white" }}
              >
                {" "}
              </BuildIcon>
              <h5 style={{ color: "white", alignSelf: "end" }}>
                {" "}
                C'è un guasto al tuo veicolo?
              </h5>
            </div>
            <h6 style={{ marginLeft: "55px" }}> Comunicalo qui </h6>
          </div>
          <div
            style={{
              gridColumn: "1",
              gridRow: "6",
              marginBottom: "20px",
              justifySelf: "center",
            }}
          >
            <Button
              href="javascript:history.back()"
              className="indietro"
              variant="primary"
            >
              INDIETRO
            </Button>
          </div>
        </div>
      );
    }
  }
}
GestioneCorsa.propTypes = {
  logoutUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps, { logoutUser })(GestioneCorsa);
