import {
  GET_ERRORS_METODIPAGAMENTO,
  VISUALIZZA_METODIPAGAMENTO,
  CANCELLA_METODIPAGAMENTO,
} from "../actions/type";

const INITIAL_STATE = {
  metodopagamento: [],
  metodipagamento: {},
  error: null,
};

export default (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case VISUALIZZA_METODIPAGAMENTO:
      return {
        ...state,
        metodopagamento: action.payload,
      };
    case GET_ERRORS_METODIPAGAMENTO:
      return {
        ...state,
        error: action.error,
      };
    case CANCELLA_METODIPAGAMENTO:
      const metodopagamento = state.metodopagamento.filter(
        (metodopagamento) => metodopagamento._id !== action.payload.id
      );
      return {
        ...state,
        deleted: true,
        metodopagamento: metodopagamento,
      };
    default:
      return state;
  }
};
