import React from 'react';
import { BrowserRouter, Route, Switch} from 'react-router-dom';
import jwt_decode from "jwt-decode";
import setAuthToken from "./utils/setAuthToken";
import { setCurrentUser, logoutUser } from "../src/actions/authAutentications";

//store
import { Provider } from "react-redux";
import store from "./store";

//css
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

//componenti
import Layout from './componenti/Layout/layout/layout';
import LayoutLogin from './componenti/Layout login/layout/layout'
import PrivateRoute from "../src/componenti/private-route/PrivateRoute";
import Dashboard from "../src/componenti/dashboard/Dashboard";

//pagine
import Register from './componenti/auth/Register';
import Login from './componenti/auth/Login';

// Check for token to keep user logged in
if (localStorage.jwtToken) {
  // Set auth token header auth
  const token = localStorage.jwtToken;
  setAuthToken(token);
  // Decode token and get user info and exp
  const decoded = jwt_decode(token);
  // Set user and isAuthenticated
  store.dispatch(setCurrentUser(decoded));
// Check for expired token
  const currentTime = Date.now() / 1000; // to get in milliseconds
  if (decoded.exp < currentTime) {
    // Logout user
    store.dispatch(logoutUser());
    // Redirect to login
    window.location.href = "./login";
  }
}

function App() {
  return (
    <Provider store={store}>
    <BrowserRouter>
      <LayoutLogin>
      <Route exact path="/" component={Login} />
      <Route exact path="/register" component={Register} />
      <Route exact path="/login" component={Login} />
      </LayoutLogin>
      <Layout>
        <Switch>
          <PrivateRoute exact path="/dashboard" component={Dashboard} />
        </Switch>  
      </Layout>    
    </BrowserRouter>
    </Provider>
  );
}

export default App;
