import React, { Component } from "react";
import { Link, withRouter } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import {
  loginUser,
  logoutUser,
  recuperoPassword,
  codiceOTP,
  modificaPassword2,
} from "../actions/authAutentications";
import classnames from "classnames";

//componenti da react-bootstrap
import { Container } from "react-bootstrap";

class ModificaPassword extends Component {
  constructor() {
    super();
    this.state = {
      password: "",
      password2: "",
      success: "",
      errors: {},
    };
  }

  componentDidMount() {
    // If logged in and user navigates to Register page, should redirect them to dashboard
    if (this.state.success) {
      this.props.history.push("/IlMioProfilo");
    }
  }

  onChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors,
      });
    }
  }

  onSubmit = (e) => {
    e.preventDefault();

    const dataUser = {
      email: this.state.email,
      password: this.state.password,
      password2: this.state.password2,
    };
    this.props.modificaPassword2(this.state.email, dataUser);
    console.log(Object.keys(this.state.errors).length);
    //if (Object.keys(this.state.errors).length == 0)
    this.state.success = true;
    //aggiungere notifica
    //aggiungere notifica errore
  };

  render() {
    const { errors } = this.state;
    const { user } = this.props.auth;
    this.state.email = user.email;
    if (this.state.success) this.props.history.push("/IlMioProfilo");
    return (
      <Container style={{ marginTop: "12rem", marginBottom: "15rem" }}>
        <div className="Form">
          <form noValidate onSubmit={this.onSubmit}>
            <h3 style={{ textAlign: "center", color: "black" }}>
              Inserisci la nuova password
            </h3>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                onChange={this.onChange}
                value={this.state.password}
                error={errors.password}
                id="password"
                type="password"
                className={classnames("form-control", {
                  invalid: errors.password,
                })}
              />
              <span className="red-text">{errors.password}</span>
            </div>
            <div className="form-group">
              <label htmlFor="password2">Conferma Password</label>
              <input
                onChange={this.onChange}
                value={this.state.password2}
                error={errors.password2}
                id="password2"
                type="password"
                className={classnames("form-control", {
                  invalid: errors.password2,
                })}
              />
              <span className="red-text">{errors.password2}</span>
            </div>
            <button
              type="submit"
              value="Registrazione Log"
              className="btn btn-dark btn-lg btn-block bottone"
            >
              Conferma
            </button>
          </form>
        </div>
      </Container>
    );
  }
}

ModificaPassword.propTypes = {
  loginUser: PropTypes.func.isRequired,
  logoutUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  modificaPassword2: PropTypes.func.isRequired,
  errors: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
  errors: state.errors,
});

export default connect(mapStateToProps, {
  loginUser,
  modificaPassword2,
  logoutUser,
})(withRouter(ModificaPassword));
