import React, { Component } from "react";
import { inviaNotificheAutisti } from "../actions/prenotazioniActions";
import { ListaNotifiche } from "../actions/notificheActions";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { Link, withRouter } from "react-router-dom";

//componenti da react-bootstrap
import {
  Container,
  Col,
  Row,
  Modal,
  Button,
  Card,
  Toast,
} from "react-bootstrap";

import Logo from "../immagini/logo1.png";

import "../App.css";
import classnames from "classnames";

class NotificheLista extends Component {
  state = {
    errors: {},
    current_id: "",
  };

  handleChange = (name, value) => {
    this.setState({ [name]: value });
  };

  async componentDidMount() {
    await this.props.ListaNotifiche();
  }
  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors,
      });
    }
  }

  onChange = async (e) => {
    await this.setState({ [e.target.id]: e.target.value });
  };

  onSubmit = (e) => {
    e.preventDefault();
  };

  render() {
    const { errors } = this.state;
    const { notifica } = this.props.listanotifiche;
    const today = new Date();
    const NL = (
      <Container sm={12} style={{ marginBottom: "21rem" }}>
        <div
          class="center"
          style={{ textAlign: "left", display: "flex", flexWrap: "wrap" }}
        >
          {notifica.map((notifica) => (
            <Col xs={6}>
              <Toast show={true}>
                <Toast.Header>
                  <img src={Logo} className="rounded mr-2" alt="logo-karm" />
                  <strong className="mr-auto">{notifica.titolo}</strong>
                  <small>{notifica.createAt}</small>
                </Toast.Header>
                <Toast.Body>{notifica.descrizione}</Toast.Body>
              </Toast>
            </Col>
          ))}
        </div>
      </Container>
    );
    return (
      <Container fluid style={{ marginBottom: "3rem" }}>
        <Container sm={12}>
          <Row>
            <Col sm>
              <div className="form-group">
                <label htmlFor="modello">Modello</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroModello", event.target.value)
                  }
                  id="modello"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="cilindrata">Cilindrata</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroCilindrata", event.target.value)
                  }
                  id="cilindrata"
                  type="text"
                />
              </div>
            </Col>
          </Row>
          <h1
            style={{
              color: "#010101",
              fontWeight: "bold",
              fontSize: "25px",
              textAlign: "center",
            }}
          >
            Notifiche
          </h1>
          {notifica.length === 0
            ? "Non ci sono veicoli per la tipologia scelta disponibili"
            : NL}
        </Container>
      </Container>
    );
  }
}

NotificheLista.propTypes = {
  ListaNotifiche: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired,
  listanotifiche: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
  errors: state.errors,
});
export default connect(mapStateToProps, {
  inviaNotificheAutisti,
  ListaNotifiche,
})(NotificheLista);
