import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { Link, withRouter } from "react-router-dom";
import { ListaPrenotazioni } from "../actions/prenotazioniActions";

//componenti da react-bootstrap
import { Container, Col, Row, Modal, Button } from "react-bootstrap";
import "../App.css";
import classnames from "classnames";

import ricercaFiltro from "../utils/ricercaFiltro";

class VisualizzaPrenotazioni extends Component {
  state = {
    filtroNome: "",
    filtroCognome: "",
    filtroEmail: "",
    filtroFiscale: "",
    errors: {},
  };

  async componentDidMount() {
    await this.props.ListaPrenotazioni();
  }
  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors,
      });
    }
  }

  handleChange = (name, value) => {
    this.setState({ [name]: value });
  };

  onChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };

  setModalShow = (input) => {
    this.setState({ modalShow: input });
  };

  render() {
    const { filtroNome, filtroCognome, filtroEmail, filtroFiscale, errors } =
      this.state;
    const { prenotazione } = this.props.listaprenotazioni;
    let prenotazioniFiltrate = [];
    prenotazioniFiltrate = ricercaFiltro(prenotazione, filtroStato, "stato");

    const PRL = (
      <div className="container" style={{ marginBottom: "14rem" }}>
        <div class="center" style={{ textAlign: "left" }}>
          <table class="responsive-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Cognome</th>
                <th>Codice Fiscale</th>
                <th>Email</th>
                <th>Ruolo</th>
                <th>Elimina</th>
              </tr>
            </thead>
            <tbody>
              {prenotazioniFiltrate.map((prenotazione) => (
                <tr key={prenotazione._id}>
                  <td>{prenotazione._id}</td>

                  <td>
                    {" "}
                    <button
                      class="btn"
                      onClick={() => this.deleteDipendente(prenotazione._id)}
                    >
                      <i class="material-icons">delete</i>
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );

    return (
      <Container fluid style={{ marginTop: "3rem", marginBottom: "3rem" }}>
        <div className="row">
          {prenotazione.length === 0 ? "Non ci sono prenotazioni" : PRL}
        </div>
      </Container>
    );
  }
}

VisualizzaPrenotazioni.propTypes = {
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired,
  listaprenotazioni: PropTypes.object.isRequired,
  ListaPrenotazioni: PropTypes.func.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
  errors: state.errors,
  listaprenotazioni: state.listaprenotazioni,
});
export default connect(mapStateToProps, {
  ListaPrenotazioni,
})(withRouter(VisualizzaPrenotazioni));
