import { Listametodipagamento } from "../../actions/authAutentications";
import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { Link, withRouter } from "react-router-dom";
import {
  registerMetodoPagamento,
  CancellaMetodoPagamento,
} from "../../actions/authAutentications";

//componenti da react-bootstrap
import { Container, Col, Row, Modal, Button } from "react-bootstrap";
import "../../App.css";
import classnames from "classnames";

class MetodiPagamento extends Component {
  state = {
    id_cliente: "",
    numero_carta: "",
    intestatario: "",
    data_scadenza: "",
    cvv: "",
    errors: {},
  };

  handleChange = (name, value) => {
    this.setState({ [name]: value });
  };

  deleteMetodoPagamento(id) {
    this.props.CancellaMetodoPagamento(id);
    window.location.reload();
  }

  onChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };

  onSubmit = (e) => {
    e.preventDefault();

    const newMetodoPagamento = {
      name: this.state.name,
      id_cliente: this.state.id_cliente,
      numero_carta: this.state.numero_carta,
      intestatario: this.state.intestatario,
      data_scadenza: this.state.data_scadenza,
      cvv: this.state.cvv,
    };
    this.props.registerMetodoPagamento(newMetodoPagamento, this.props.history);
    this.setModalShow(false);
    window.location.reload();
  };

  async componentDidMount() {
    await this.props.Listametodipagamento();
  }

  viewCustomer(id) {
    console.log(id);
  }
  setModalShow = (input) => {
    this.setState({ modalShow: input });
  };

  render() {
    const { errors } = this.state;
    const { metodipagamento } = this.props.listametodipagamento;

    const MP = (
      <div className="container">
        <div class="center" style={{ textAlign: "left" }}>
          <table class="responsive-table">
            <thead>
              <tr>
                <th>Numero Carta</th>
                <th>intestatario</th>
                <th>Data Scadenza</th>
                <th>CVV</th>
                <th>Elimina</th>
              </tr>
            </thead>
            <tbody>
              {listametodipagamento.map((metodipagamento) => (
                <tr key={metodipagamento._id}>
                  <td>{metodipagamento.numero_carta}</td>
                  <td>{metodipagamento.intestatario}</td>
                  <td>{metodipagamento.data_scadenza}</td>
                  <td>{metodipagamento.cvv}</td>
                  <td>
                    {" "}
                    <button
                      class="btn"
                      onClick={() => this.deleteMetodoPagamento(user._id)}
                    >
                      <i class="material-icons">delete</i>
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );

    return (
      <Container fluid style={{ marginTop: "3rem", marginBottom: "3rem" }}>
        <Modal
          {...this.props}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          centered
          show={this.state.modalShow}
          onHide={() => this.setModalShow(false)}
          backdrop="static"
        >
          <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
              Inserisci i dati del metodo di pagamento
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Container style={{ marginTop: "3rem", marginBottom: "3rem" }}>
              <div className="Form">
                <form noValidate onSubmit={this.onSubmit}>
                  <div className="form-group">
                    <label htmlFor="numero_carta">Numero di carta</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.numero_carta}
                      error={errors.numero_carta}
                      id="numero_carta"
                      type="text"
                      className={classnames("form-control", {
                        invalid: errors.numero_carta,
                      })}
                    />
                    <span className="red-text">{errors.numero_carta}</span>
                  </div>
                  <div className="form-group">
                    <label htmlFor="intestatario">intestatario</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.intestatario}
                      error={errors.intestatario}
                      id="intestatario"
                      type="text"
                      className={classnames("form-control", {
                        invalid: errors.intestatario,
                      })}
                    />
                    <span className="red-text">{errors.intestatario}</span>
                  </div>
                  <div className="form-group">
                    <label htmlFor="data_scadenza">Data di scadenza</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.data_scadenza}
                      error={errors.data_scadenza}
                      id="data_scadenza"
                      type="text"
                      className={classnames("form-control", {
                        invalid: errors.data_scadenza,
                      })}
                    />
                    <span className="red-text">{errors.data_scadenza}</span>
                  </div>
                  <div className="form-group">
                    <label htmlFor="cvv">CVV</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.cvv}
                      error={errors.cvv}
                      id="cvv"
                      type="text"
                      className={classnames("form-control", {
                        invalid: errors.cvv,
                      })}
                    />
                    <span className="red-text">{errors.cvv}</span>
                  </div>
                  <button
                    type="submit"
                    value="Registrazione Log"
                    className="btn btn-dark btn-lg btn-block bottone"
                  >
                    Inserisci
                  </button>
                </form>
              </div>
            </Container>
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={() => this.setModalShow(false)}>Annulla</Button>
          </Modal.Footer>
        </Modal>
        <div className="Form">
          <Row>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm>
              <button
                className="btn btn-dark btn-lg btn-block bottone"
                onClick={() => this.setModalShow(true)}
              >
                + Inserisci nuovo metodo di pagamento
              </button>
            </Col>
          </Row>
        </div>
        <div className="row">
          {metodipagamento.length === 0
            ? "Non ci sono metodi di pagamento"
            : MP}
        </div>
      </Container>
    );
  }
}

MetodiPagamento.propTypes = {
  registerMetodoPagamento: PropTypes.func.isRequired,
  CancellaMetodoPagamento: PropTypes.func.isRequired,
  Listametodipagamento: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired,
  listametodipagamento: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
  errors: state.errors,
  listametodipagamento: state.listametodipagamento,
});
export default connect(mapStateToProps, {
  registerMetodoPagamento,
  Listametodipagamento,
  CancellaMetodoPagamento,
})(withRouter(MetodiPagamento));
