import {Listaclienti} from '../../actions/authAutentications';
import React, { Component } from 'react';

import PropTypes from 'prop-types';
import { connect } from 'react-redux';

//componenti da react-bootstrap
import { Container, Col, Row } from 'react-bootstrap';

class AggiornaClienti extends Component {
  constructor(){
    super();
    this.state = {
      search: ''
    }
  }
  
  updateSearch(event){
    this.setState({ search: event.target.value.substr(0, 20)});
  }
  
 async componentDidMount() {
   await this.props.Listaclienti();
 }

viewCustomer(id){
console.log(id);

}
 
  render() {
   const {user}=this.props.customerlist;
  
	return(
    
    <Container style={{marginTop: '3rem', marginBottom: '3rem'}}>
      <div className="Form">
              <Row>
                <Col sm={2}>
              <div className="form-group">
              <label htmlFor="name">Nome</label>
                <input
                  value={this.state.search}
                  onChange={this.updateSearch.bind}
                  id="name"
                  type="text"
                />
              </div>
              </Col>
              <Col sm={2}>
              <div className="form-group">
              <label htmlFor="cognome">Cognome</label>
                <input

                  id="cognome"
                  type="text"
                />
              </div>
              </Col>
              <Col sm={2}>
              <div className="form-group">
              <label htmlFor="email">Email</label>
                <input

                  id="email"
                  type="text"
                />
              </div>
              </Col>
              <Col sm={2}>
              <div className="form-group">
              <label htmlFor="numero">Numero di telefono</label>
                <input

                  id="numero"
                  type="text"
                />
              </div>
              </Col>
              <Col sm={2}>
              <div className="form-group">
              <label htmlFor="cf">Codice Fiscale</label>
                <input

                  id="cf"
                  type="text"
                />
              </div>
              </Col>
             </Row>

          </div>
          <div className="row">
          <ul>
          {user.map((user)=> {
          return <User user={user}
          key={user._id} />
          })}
          </ul>
          </div>
          </Container>

  );
  }
}

 
   



AggiornaClienti.propTypes = {
 Listaclienti:PropTypes.func.isRequired,

 auth:PropTypes.object.isRequired,
 errors:PropTypes.object.isRequired,
customerlist:PropTypes.object.isRequired,

};
const mapStateToProps = state => ({
  auth:state.auth,
  errors:state.errors,
  customerlist:state.customerlist
});
export default connect(mapStateToProps, 
 {Listaclienti})
  (AggiornaClienti);
