import React, { Component } from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { loginUser, setCurrentUser } from "../../actions/authAutentications";
import classnames from "classnames";

//componenti da react-bootstrap
import { Container } from 'react-bootstrap';

class Login extends Component {
  constructor() {
    super();
    this.state = {
      email: "",
      password: "",
      ruolo: "",
      errors: {}
    };
  }

  componentDidMount() {
    //se sei autenticato e provi ad andare indietro
    // If logged in and user navigates to Login page, should redirect them to dashboard
    
    axios.get('http://localhost:5000/users/')
      .then(response => {
        if (response.data.length > 0) {
          this.setState({
            users: response.data.map(user => user.username),
            ruolo: response.data[0].ruolo
          })
        }
      })
      .catch((error) => {
        console.log(error);
      })
      if (this.props.auth.isAuthenticated && ruolo==="Cliente") {
        this.props.history.push("/HomeCliente");
      }
  }
  //dopo autenticazione
  componentWillReceiveProps(nextProps) {
    if (nextProps.auth.isAuthenticated) {
      if(setCurrentUser.ruolo==='Cliente')
      this.props.history.push("/HomeCliente");
    }

    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors
      });
    }
  }

  onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };

  onSubmit = e => {
    e.preventDefault();

    const userData = {
      email: this.state.email,
      password: this.state.password
    };

    this.props.loginUser(userData);
  };

  render() {
    const { errors } = this.state;

    return (
      <Container style={{marginTop: '10rem', marginBottom: '17rem'}}>
      <div className="form">
            <form noValidate onSubmit={this.onSubmit}>
            <h3 style={{textAlign: 'center'}}>Accedi</h3>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                  onChange={this.onChange}
                  value={this.state.email}
                  error={errors.email}
                  id="email"
                  type="email"
                  className={classnames("form-control", {
                    invalid: errors.email || errors.emailnotfound
                  })}
                />
                <span className="red-text">
                  {errors.email}
                  {errors.emailnotfound}
                </span>
              </div>
              <div className="form-group">
              <label htmlFor="password">Password</label>
                <input
                  onChange={this.onChange}
                  value={this.state.password}
                  error={errors.password}
                  id="password"
                  type="password"
                  className={classnames("form-control", {
                    invalid: errors.password || errors.passwordincorrect
                  })}
                />
                <span className="red-text">
                  {errors.password}
                  {errors.passwordincorrect}
                </span>
              </div>
              <button type="submit" value="Registrazione Log" className="btn btn-dark btn-lg btn-block">Accedi</button>
                <h5 style={{marginTop: '1rem'}}><p className="forgot-password text-right">
                Non hai un account? <Link to="/register">Registrati</Link> 
                </p></h5>
            </form>
          </div>
          </Container>
    );
  }
}

Login.propTypes = {
  loginUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});

export default connect(
  mapStateToProps,
  { loginUser }
)(Login);
