import React, { Component } from "react";
import {
  GetSpecificVeicolo,
  ModificaVeicolo,
} from "../../actions/veicoliActions";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import "../../App.css";
import classnames from "classnames";

import { Container, Col, Row, Modal, Button } from "react-bootstrap";

class ModificaVeicoli extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tipo: this.props.listaveicoli.tipo,
      modello: this.props.listaveicoli.modello,
      cilindrata: this.props.listaveicoli.cilindrata,
      patente_necessaria: this.props.listaveicoli.patente_necessaria,
      modalShow: true,
      errors: {},
    };
  }
  setModalShow = (input) => {
    this.setState({ modalShow: input });
  };
  componentDidMount() {
    this.props.GetSpecificVeicolo(this.props.match.params.id);
  }
  onChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };
  onSubmit = (e, id) => {
    e.preventDefault();
    const veicolo = {
      tipo: this.state.tipo,
      modello: this.state.modello,
      cilindrata: this.state.cilindrata,
      patente_necessaria: this.state.patente_necessaria,
    };

    this.props.ModificaVeicolo(this.props.match.params.id, veicolo);
    this.setModalShow(false);

    this.props.history.push("/AggiornaVeicoli");
    window.location.reload();
  };

  render() {
    const { veicolo } = this.props.listaveicoli;
    const { errors } = this.state;
    return (
      <Container fluid style={{ marginTop: "3rem", marginBottom: "3rem" }}>
        <Modal
          {...this.props}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          centered
          show={this.state.modalShow}
          onHide={() => this.setModalShow(false)}
          backdrop="static"
        >
          <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
              Inserisci i dati del veicolo
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Container>
              <div className="Form">
                <form noValidate onSubmit={this.onSubmit}>
                  <div className="form-group">
                    <label>Tipo</label>
                    <select
                      id="tipo"
                      type="text"
                      value={this.state.tipo}
                      placeholder={veicolo.tipo}
                      error={errors.tipo}
                      onChange={this.onChange}
                      className={classnames("form-control", {
                        invalid: errors.tipo,
                      })}
                    >
                      <option value="Auto">Auto</option>
                      <option value="Moto">Moto</option>
                      <option value="Bicicletta">Bicicletta</option>
                      <option value="Monopattino">Monopattino</option>
                    </select>
                    <span className="red-text">{errors.tipo}</span>
                  </div>
                  <div className="form-group">
                    <label htmlFor="modello">Modello</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.modello}
                      id="modello"
                      error={errors.modello}
                      placeholder={veicolo.modello}
                      type="text"
                      className={classnames("form-control", {
                        invalid: errors.modello,
                      })}
                    />
                    <span className="red-text">{errors.modello}</span>
                  </div>
                  <div className="form-group">
                    <label htmlFor="cilindrata">Cilindrata</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.cilindrata}
                      placeholder={veicolo.cilindrata}
                      error={errors.cilindrata}
                      id="cilindrata"
                      type="number"
                      className={classnames("form-control", {
                        invalid: errors.cilindrata,
                      })}
                    />
                    <span className="red-text">{errors.cilindrata}</span>
                  </div>
                  <div className="form-group">
                    <label>Patente Necessaria</label>
                    <select
                      id="patente_necessaria"
                      type="text"
                      value={this.state.patente_necessaria}
                      placeholder={veicolo.patente_necessaria}
                      onChange={this.onChange}
                      error={errors.patente_necessaria}
                      className={classnames("form-control", {
                        invalid: errors.patente_necessaria,
                      })}
                    >
                      <option value="" disabled selected>
                        Patente
                      </option>
                      <option value="AM">AM</option>
                      <option value="A1">A1</option>
                      <option value="A2">A2</option>
                      <option value="B">B</option>
                      <option value="C">C</option>
                      <option value="NaN">Nessuna</option>
                    </select>
                    <span className="red-text">
                      {errors.patente_necessaria}
                    </span>
                  </div>
                  <button
                    type="submit"
                    value="Aggiunta Veicolo"
                    className="btn btn-dark btn-lg btn-block bottone"
                  >
                    Aggiorna
                  </button>
                </form>
              </div>
            </Container>
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={() => this.props.history.push("/AggiornaVeicoli")}>
              Annulla
            </Button>
          </Modal.Footer>
        </Modal>
        <div className="Form">
          <Row>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm>
              <button
                className="btn btn-dark btn-lg btn-block bottone"
                onClick={() => this.setModalShow(true)}
              >
                + Inserisci Veicolo
              </button>
            </Col>
          </Row>
          <Row>
            <Col sm>
              <div className="form-group">
                <label htmlFor="tipo">Tipo</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroTipo", event.target.value)
                  }
                  id="tipo"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="modello">Modello</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroModello", event.target.value)
                  }
                  id="modello"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="cilindrata">Cilindrata</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroCilindrata", event.target.value)
                  }
                  id="cilindrata"
                  type="text"
                />
              </div>
            </Col>
          </Row>
        </div>
      </Container>
    );
  }
}

ModificaVeicoli.propTypes = {
  ModificaVeicolo: PropTypes.func.isRequired,
  GetSpecificVeicolo: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired,
  listaveicoli: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
  errors: state.errors,
  listaveicoli: state.listaveicoli,
});
export default connect(mapStateToProps, {
  GetSpecificVeicolo,
  ModificaVeicolo,
})(ModificaVeicoli);
