import React, { Component } from "react";
import { Link, withRouter } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { registerUser } from "../../actions/authAutentications";
import classnames from "classnames";
import { Container } from 'react-bootstrap';

class Register extends Component {
  constructor() {
    super();
    this.state = {
        nome: '',
        cognome: '',
        cf: '',
        sesso: '',
        luogo_nascita: '',
        provincia_nascita: '',
        email: '',
        password: '',
        password2: '',
        patente: '',
      errors: {}
    };
  }

componentDidMount() {
    // If logged in and user navigates to Register page, should redirect them to dashboard
    if (this.props.auth.isAuthenticated) {
      this.props.history.push("/dashboard");
    }
  }

componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors
      });
    }
  }
onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };
onSubmit = e => {
    e.preventDefault();
const newCliente = {
    nome: this.state.nome,
    cognome: this.state.cognome,
    cf: this.state.cf,
    sesso: this.state.sesso,
    luogo_nascita: this.state.luogo_nascita,
    email: this.state.email,
    password: this.state.password,
    password2: this.state.password2,
    patente: this.state.patente
    };
this.props.registerCliente(newCliente, this.props.history); 
  };
render() {
    const { errors } = this.state;
return (
      <div className="Form">
        <Container>        
            <h3 style={{textAlign: 'center'}}>Registrati</h3>
            <form noValidate onSubmit={this.onSubmit}>
              <div className="form-group">
              <label htmlFor="nome">Nome</label>
                <input
                  onChange={this.onChange}
                  value={this.state.nome}
                  error={errors.nome}
                  id="nome"
                  type="text"
                  className={classnames("form-control", {
                    invalid: errors.nome
                  })}
                />
                <span className="red-text">{errors.nome}</span>
              </div>
              <div className="form-group">
              <label htmlFor="cf">Codice Fiscale</label>
                <input
                  onChange={this.onChange}
                  value={this.state.cf}
                  error={errors.cf}
                  id="cf"
                  type="text"
                  className={classnames("form-control", {
                    invalid: errors.cf
                  })}
                />
                <span className="red-text">{errors.cf}</span>
              </div>
              <div className="form-group">
              <label htmlFor="luogo_nascita">Luogo di Nascita</label>
                <input
                  onChange={this.onChange}
                  value={this.state.luogo_nascita}
                  error={errors.luogo_nascita}
                  id="luogo_nascita"
                  type="text"
                  className={classnames("form-control", {
                    invalid: errors.luogo_nascita
                  })}
                />
                <span className="red-text">{errors.luogo_nascita}</span>
              </div>
              <div className="form-group">
                    <label htmlFor="provincia_nascito">Provincia Nascita</label>
                    <select className="form-control" type="text" value={this.state.provincia_nascita} onChange={this.onChange}>
                    <option value="Agrigento">Agrigento</option>
                    <option value="Caltanissetta">Caltanissetta</option>
                    <option value="Catania">Catania</option>
                    <option value="Enna">Enna</option>
                    <option value="Messina">Messina</option>
                    <option value="Palermo">Palermo</option>
                    <option value="Ragusa">Ragusa</option>
                    <option value="Siracusa">Siracusa</option>
                    <option value="Trapani">Trapani</option>
                    </select>
              </div>
              <div className="form-group">
              <label htmlFor="patente">Patente</label>
                <input
                  onChange={this.onChange}
                  value={this.state.patente}
                  error={errors.patente}
                  id="patente"
                  type="text"
                  className={classnames("form-control", {
                    invalid: errors.patente
                  })}
                />
                <span className="red-text">{errors.patente}</span>
              </div>
              <div className="form-group">
                    <label htmlFor="sesso">sesso</label>
                    <select className="form-control" type="text" value={this.state.sesso} onChange={this.onChange}>
                    <option value="Uomo">Uomo</option>
                    <option value="Donna">Donna</option>
                    <option value="Nan">Non specifico</option>
                    </select>
              </div>
              <div className="form-group">
              <label htmlFor="email">Email</label>
                <input
                  onChange={this.onChange}
                  value={this.state.email}
                  error={errors.email}
                  id="email"
                  type="email"
                  className={classnames("", {
                    invalid: errors.email
                  })}
                />
                <span className="red-text">{errors.email}</span>
              </div>
              <div className="form-group">
              <label htmlFor="password">Password</label>
                <input
                  onChange={this.onChange}
                  value={this.state.password}
                  error={errors.password}
                  id="password"
                  type="password"
                  className={classnames("", {
                    invalid: errors.password
                  })}
                />
                <span className="red-text">{errors.password}</span>
              </div>
              <div className="form-group">
              <label htmlFor="password2">Conferma Password</label>
                <input
                  onChange={this.onChange}
                  value={this.state.password2}
                  error={errors.password2}
                  id="password2"
                  type="password"
                  className={classnames("", {
                    invalid: errors.password2
                  })}
                />
                <span className="red-text">{errors.password2}</span>
              </div>
              <button type="submit" value="Registrazione Log" className="btn btn-dark btn-lg btn-block">Registrati</button>
            </form>
            </Container>
      </div>
    );
  }
}
Register.propTypes = {
  registerUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};
const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});
export default connect(
  mapStateToProps,
  { registerUser }
)(withRouter(Register));
