import { Listaveicoli } from "../../actions/veicoliActions";
import React, { Component } from "react";

import PropTypes from "prop-types";
import { connect } from "react-redux";
import { Link, withRouter } from "react-router-dom";

import {
  registerVeicolo,
  ModificaVeicolo,
  CancellaVeicolo,
  GetSpecificVeicolo,
} from "../../actions/veicoliActions";

//componenti da react-bootstrap
import { Container, Col, Row, Modal, Button } from "react-bootstrap";

import "../../App.css";
import classnames from "classnames";

import filtriVeicoli from "../../utils/filtriVeicoli";

class AggiornaVeicoli extends Component {
  state = {
    tipo: "",
    modello: "",
    marca: "",
    cilindrata: 0,
    n_posti: 0,
    n_porte: 0,
    targa: "",
    id_parcheggio: "",
    descrizione: "",
    prezzo_festivo: "",
    prezzo_feriale: "",
    stato: "",
    patente_necessaria: "",
    stato: "",
    type_auto: "false",
    type_moto: "false",
    type_bici_mono: "false",
    errors: {},
    filtroTipo: "",
    filtroParcheggio: "",
    filtroID: "",
  };

  async componentDidMount() {
    await this.props.Listaveicoli();
  }
  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors,
      });
    }
  }

  handleChange = (name, value) => {
    this.setState({ [name]: value });
  };
  deleteVeicolo(id) {
    this.props.CancellaVeicolo(id);
    window.location.reload();
  }

  modificaVeicolo(id) {
    this.props.history.push(`veicoli/update/${id}`);
  }

  onChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };

  onSubmit = (e) => {
    e.preventDefault();

    const newVeicolo = {
      tipo: this.state.tipo,
      modello: this.state.modello,
      marca: this.state.marca,
      cilindrata: this.state.cilindrata,
      n_posti: this.state.n_posti,
      n_porte: this.state.n_porte,
      targa: this.state.targa,
      id_parcheggio: this.state.id_parcheggio,
      descrizione: this.state.descrizione,
      prezzo_festivo: this.state.prezzo_festivo,
      prezzo_feriale: this.state.prezzo_feriale,
      stato: this.state.stato,
    };
    this.props.registerVeicolo(newVeicolo, this.props.history);
    this.setModalShow(false);
  };

  render() {
    const { filtroTipo, filtroParcheggio, filtroID, errors } = this.state;

    let veicoliFiltrati = [];
    const { veicolo } = this.props.listaveicoli;
    veicoliFiltrati = filtriVeicoli(veicolo, filtroTipo, "tipo");
    veicoliFiltrati = filtriVeicoli(
      veicoliFiltrati,
      filtroParcheggio,
      "id_parcehggio"
    );
    veicoliFiltrati = filtriVeicoli(veicoliFiltrati, filtroID, "_id");

    const VL = (
      <div className="container">
        <div class="center" style={{ textAlign: "left" }}>
          <table class="responsive-table">
            <thead>
              <tr>
                <th>Tipo</th>
                <th>Modello</th>
                <th>Marca</th>
                <th>Cilindrata</th>
                <th>Numero posti</th>
                <th>Numero porte</th>
                <th>Targa</th>
                <th>Parcheggio</th>
                <th>Prezzo Festivo</th>
                <th>Prezzo Feriale</th>
                <th>Stato</th>
                <th>Modifica</th>
              </tr>
            </thead>
            <tbody>
              {veicoliFiltrati.map((veicolo) => (
                <tr key={veicolo._id}>
                  <td>{veicolo.tipo}</td>
                  <td>{veicolo.modello}</td>
                  <td>{veicolo.marca}</td>
                  <td>{veicolo.cilindrata}</td>
                  <td>{veicolo.n_posti}</td>
                  <td>{veicolo.n_porte}</td>
                  <td>{veicolo.targa}</td>
                  <td>{veicolo.id_parcheggio}</td>
                  <td>{veicolo.prezzo_festivo}</td>
                  <td>{veicolo.prezzo_feriale}</td>
                  <td>{veicolo.stato}</td>
                  <td>
                    {" "}
                    <button
                      class="btn"
                      onClick={() => this.modificaVeicolo(veicolo._id)}
                    >
                      <i class="material-icons">create</i>
                    </button>
                    <button
                      class="btn"
                      onClick={() => this.deleteVeicolo(veicolo._id)}
                    >
                      <i class="material-icons">delete</i>
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );

    return (
      <Container fluid style={{ marginTop: "3rem", marginBottom: "3rem" }}>
        <Modal
          {...this.props}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          centered
          show={this.state.modalShow}
          onHide={() => this.setModalShow(false)}
          backdrop="static"
        >
          <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
              Inserisci i dati del veicolo
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Container>
              <div className="Form">
                <form noValidate onSubmit={this.onSubmit}>
                  <div className="form-group">
                    <label>Tipo</label>
                    <select
                      id="tipo"
                      type="text"
                      value={this.state.tipo}
                      error={errors.tipo}
                      onChange={this.onChange}
                      className={classnames("form-control", {
                        invalid: errors.tipo,
                      })}
                    >
                      <option value="" disabled selected>
                        Tipo
                      </option>
                      <option value="Auto">Auto</option>
                      <option value="Moto">Moto</option>
                      <option value="Bicicletta">Bicicletta</option>
                      <option value="Monopattino">Monopattino</option>
                    </select>
                    <span className="red-text">{errors.tipo}</span>
                  </div>
                  <div className="form-group">
                    <label htmlFor="modello">Modello</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.modello}
                      id="modello"
                      error={errors.modello}
                      type="text"
                      className={classnames("form-control", {
                        invalid: errors.modello,
                      })}
                    />
                    <span className="red-text">{errors.modello}</span>
                  </div>
                  <div className="form-group">
                    <label htmlFor="cilindrata">Cilindrata</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.cilindrata}
                      error={errors.cilindrata}
                      id="cilindrata"
                      type="number"
                      className={classnames("form-control", {
                        invalid: errors.cilindrata,
                      })}
                    />
                    <span className="red-text">{errors.cilindrata}</span>
                  </div>
                  <div className="form-group">
                    <label>Patente Necessaria</label>
                    <select
                      id="patente_necessaria"
                      type="text"
                      value={this.state.patente_necessaria}
                      onChange={this.onChange}
                      error={errors.patente_necessaria}
                      className={classnames("form-control", {
                        invalid: errors.patente_necessaria,
                      })}
                    >
                      <option value="" disabled selected>
                        Patente
                      </option>
                      <option value="AM">AM</option>
                      <option value="A1">A1</option>
                      <option value="A2">A2</option>
                      <option value="B">B</option>
                      <option value="C">C</option>
                      <option value="NaN">Nessuna</option>
                    </select>
                    <span className="red-text">
                      {errors.patente_necessaria}
                    </span>
                  </div>
                  <button
                    type="submit"
                    value="Aggiunta Veicolo"
                    className="btn btn-dark btn-lg btn-block bottone"
                  >
                    Inserisci
                  </button>
                </form>
              </div>
            </Container>
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={() => this.setModalShow(false)}>Annulla</Button>
          </Modal.Footer>
        </Modal>
        <div className="Form">
          <Row>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm>
              <button
                className="btn btn-dark btn-lg btn-block bottone"
                onClick={() => this.setModalShow(true)}
              >
                + Inserisci Veicolo
              </button>
            </Col>
          </Row>
          <Row>
            <Col sm>
              <div className="form-group">
                <label htmlFor="tipo">Tipo</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroTipo", event.target.value)
                  }
                  id="tipo"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="modello">Modello</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroModello", event.target.value)
                  }
                  id="modello"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="cilindrata">Cilindrata</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroCilindrata", event.target.value)
                  }
                  id="cilindrata"
                  type="text"
                />
              </div>
            </Col>
          </Row>
        </div>
        <div className="row">
          {veicolo.length === 0 ? "Non ci sono veicoli" : VL}
        </div>
      </Container>
    );
  }
}

AggiornaVeicoli.propTypes = {
  CancellaVeicolo: PropTypes.func.isRequired,
  registerVeicolo: PropTypes.func.isRequired,
  Listaveicoli: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired,
  listaveicoli: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
  errors: state.errors,
  listaveicoli: state.listaveicoli,
});
export default connect(mapStateToProps, {
  registerVeicolo,
  Listaveicoli,
  CancellaVeicolo,
})(AggiornaVeicoli);
