import axios from "axios";
import jwt_decode from "jwt-decode";
import {
  GET_ERRORS_METODIPAGAMENTO,
  VISUALIZZA_METODIPAGAMENTO,
  CANCELLA_METODIPAGAMENTO,
} from "./type";

export const Listametodipagamento = () => (dispatch) => {
  axios
    .get("/api/metodipagamento/metodipagamentolist")
    .then((res) => {
      console.log(res.data);
      dispatch({
        type: VISUALIZZA_METODIPAGAMENTO,
        payload: res.data,
      });
    })
    .catch((err) => console.error(err));
};

export const CancellaMetodoPagamento = (id) => (dispatch) => {
  axios
    .delete(`/api/metodipagamento/deleteMetodoPagamento/${id}`)
    .then((res) => {
      console.log(id);
      dispatch({
        type: CANCELLA_METODIPAGAMENTO,
        payload: id,
      });
    })
    .catch((err) =>
      dispatch({
        type: GET_ERRORS_METODIPAGAMENTO,
        payload: err.response.data,
      })
    );
};
