import { Listadipendenti } from "../../actions/authAutentications";
import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { Link, withRouter } from "react-router-dom";
import {
  registerUser,
  CancellaDipendente,
} from "../../actions/authAutentications";

//componenti da react-bootstrap
import { Container, Col, Row, Modal, Button } from "react-bootstrap";
import "../../App.css";
import classnames from "classnames";

import ricercaFiltro from "../../utils/ricercaFiltro";

class AggiornaDipendenti extends Component {
  state = {
    name: "",
    cognome: "",
    cf: "",
    sesso: "",
    luogo_nascita: "",
    provincia_nascita: "",
    patente: "",
    email: "",
    password: "",
    password2: "",
    numero_telefono: "",
    ruolo: "",
    filtroNome: "",
    filtroCognome: "",
    filtroEmail: "",
    filtroTelefono: "",
    filtroFiscale: "",
    errors: {},
  };

  handleChange = (name, value) => {
    this.setState({ [name]: value });
  };

  deleteDipendente(id) {
    this.props.CancellaDipendente(id);
    window.location.reload();
  }

  onChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };

  onSubmit = (e) => {
    e.preventDefault();

    const newUser = {
      name: this.state.name,
      cognome: this.state.cognome,
      cf: this.state.cf,
      sesso: this.state.sesso,
      luogo_nascita: this.state.luogo_nascita,
      provincia_nascita: this.state.provincia_nascita,
      patente: this.state.patente,
      email: this.state.email,
      password: this.state.password,
      numero_telefono: this.state.numero_telefono,
      ruolo: this.state.ruolo,
      password2: this.state.password2,
    };
    this.props.registerUser(newUser, this.props.history);
    this.setModalShow(false);
    window.location.reload();
  };

  async componentDidMount() {
    await this.props.Listadipendenti();
  }

  viewCustomer(id) {
    console.log(id);
  }
  setModalShow = (input) => {
    this.setState({ modalShow: input });
  };

  render() {
    const {
      filtroNome,
      filtroCognome,
      filtroEmail,
      filtroTelefono,
      filtroFiscale,
      errors,
    } = this.state;

    let utentiFiltrati = [];
    const { user } = this.props.customerlist;
    utentiFiltrati = ricercaFiltro(user, filtroNome, "name");
    utentiFiltrati = ricercaFiltro(utentiFiltrati, filtroCognome, "cognome");
    utentiFiltrati = ricercaFiltro(utentiFiltrati, filtroEmail, "email");
    utentiFiltrati = ricercaFiltro(
      utentiFiltrati,
      filtroTelefono,
      "numero_telefono"
    );
    utentiFiltrati = ricercaFiltro(utentiFiltrati, filtroFiscale, "cf");

    const CL = (
      <div className="container">
        <div class="center" style={{ textAlign: "left" }}>
          <table class="responsive-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Cognome</th>
                <th>Email</th>
                <th>Codice Fiscale</th>
                <th>Numero di Telefono</th>
                <th>Ruolo</th>
                <th>Elimina</th>
              </tr>
            </thead>
            <tbody>
              {utentiFiltrati.map((user) => (
                <tr key={user._id}>
                  <td>{user.name}</td>
                  <td>{user.cognome}</td>
                  <td>{user.email}</td>
                  <td>{user.cf}</td>
                  <td>{user.numero_telefono}</td>
                  <td>{user.ruolo}</td>
                  <td>
                    {" "}
                    <button
                      class="btn"
                      onClick={() => this.deleteDipendente(user._id)}
                    >
                      <i class="material-icons">delete</i>
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );

    return (
      <Container fluid style={{ marginTop: "3rem", marginBottom: "3rem" }}>
        <Modal
          {...this.props}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          centered
          show={this.state.modalShow}
          onHide={() => this.setModalShow(false)}
          backdrop="static"
        >
          <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
              Inserisci i dati del dipendente
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Container style={{ marginTop: "3rem", marginBottom: "3rem" }}>
              <div className="Form">
                <form noValidate onSubmit={this.onSubmit}>
                  <div className="form-group">
                    <label htmlFor="name">Nome</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.name}
                      error={errors.name}
                      id="name"
                      type="text"
                      className={classnames("form-control", {
                        invalid: errors.name,
                      })}
                    />
                    <span className="red-text">{errors.name}</span>
                  </div>
                  <div className="form-group">
                    <label htmlFor="cognome">Cognome</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.cognome}
                      error={errors.cognome}
                      id="cognome"
                      type="text"
                      className={classnames("form-control", {
                        invalid: errors.cognome,
                      })}
                    />
                    <span className="red-text">{errors.cognome}</span>
                  </div>
                  <div className="form-group">
                    <label htmlFor="cf">Codice Fiscale</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.cf}
                      error={errors.cf}
                      id="cf"
                      type="text"
                      className={classnames("form-control", {
                        invalid: errors.cf,
                      })}
                    />
                    <span className="red-text">{errors.cf}</span>
                  </div>
                  <div className="form-group">
                    <label>Sesso</label>
                    <select
                      id="sesso"
                      type="text"
                      value={this.state.sesso}
                      onChange={this.onChange}
                      error={errors.sesso}
                      className={classnames("form-control", {
                        invalid: errors.sesso,
                      })}
                    >
                      <option value="" disabled selected>
                        Sesso
                      </option>
                      <option value="Uomo">Uomo</option>
                      <option value="Donna">Donna</option>
                      <option value="NaN">Non specifico</option>
                    </select>
                    <span className="red-text">{errors.sesso}</span>
                  </div>
                  <div className="form-group">
                    <label htmlFor="luogo_nascita">Luogo di nascita</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.luogo_nascita}
                      error={errors.luogo_nascita}
                      id="luogo_nascita"
                      type="text"
                      className={classnames("form-control", {
                        invalid: errors.luogo_nascita,
                      })}
                    />
                    <span className="red-text">{errors.luogo_nascita}</span>
                  </div>
                  <div className="form-group">
                    <label>Provincia di nascita</label>
                    <select
                      id="provincia_nascita"
                      type="text"
                      value={this.state.provincia_nascita}
                      onChange={this.onChange}
                      error={errors.provincia_nascita}
                      className={classnames("form-control", {
                        invalid: errors.provincia_nascita,
                      })}
                    >
                      <option value="" disabled selected>
                        Provincia di Nascita
                      </option>
                      <option value="Agrigento">Agrigento</option>
                      <option value="Caltanissetta">Caltanissetta</option>
                      <option value="Catania">Catania</option>
                      <option value="Enna">Enna</option>
                      <option value="Messina">Messina</option>
                      <option value="Palermo">Palermo</option>
                      <option value="Ragusa">Ragusa</option>
                      <option value="Siracusa">Siracusa</option>
                      <option value="Trapani">Trapani</option>
                    </select>
                    <span className="red-text">{errors.provincia_nascita}</span>
                  </div>
                  <div className="form-group">
                    <label htmlFor="numero_telefono">Numero di telefono</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.numero_telefono}
                      error={errors.numero_telefono}
                      id="numero_telefono"
                      type="text"
                      className={classnames("form-control", {
                        invalid: errors.numero_telefono,
                      })}
                    />
                    <span className="red-text">{errors.luogo_nascita}</span>
                  </div>
                  <div className="form-group">
                    <label htmlFor="email">Email</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.email}
                      error={errors.email}
                      id="email"
                      type="email"
                      className={classnames("form-control", {
                        invalid: errors.email,
                      })}
                    />
                    <span className="red-text">{errors.email}</span>
                  </div>
                  <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.password}
                      error={errors.password}
                      id="password"
                      type="password"
                      className={classnames("form-control", {
                        invalid: errors.password,
                      })}
                    />
                    <span className="red-text">{errors.password}</span>
                  </div>
                  <div className="form-group">
                    <label htmlFor="password2">Conferma Password</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.password2}
                      error={errors.password2}
                      id="password2"
                      type="password"
                      className={classnames("form-control", {
                        invalid: errors.password2,
                      })}
                    />
                    <span className="red-text">{errors.password2}</span>
                  </div>
                  <div className="form-group">
                    <label>Ruolo</label>
                    <select
                      id="ruolo"
                      type="text"
                      value={this.state.ruolo}
                      onChange={this.onChange}
                      error={errors.ruolo}
                      className={classnames("form-control", {
                        invalid: errors.ruolo,
                      })}
                    >
                      <option value="" disabled selected>
                        Ruolo
                      </option>
                      <option value="Addetto">Addetto al parcheggio</option>
                      <option value="Autista">Autista</option>
                    </select>
                    <span className="red-text">{errors.ruolo}</span>
                  </div>
                  <button
                    type="submit"
                    value="Registrazione Log"
                    className="btn btn-dark btn-lg btn-block bottone"
                  >
                    Inserisci
                  </button>
                </form>
              </div>
            </Container>
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={() => this.setModalShow(false)}>Annulla</Button>
          </Modal.Footer>
        </Modal>
        <div className="Form">
          <Row>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm>
              <button
                className="btn btn-dark btn-lg btn-block bottone"
                onClick={() => this.setModalShow(true)}
              >
                + Inserisci dipendente
              </button>
            </Col>
          </Row>
          <Row>
            <Col sm>
              <div className="form-group">
                <label htmlFor="nome">Nome</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroNome", event.target.value)
                  }
                  id="nome"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="cognome">Cognome</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroCognome", event.target.value)
                  }
                  id="cognome"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroEmail", event.target.value)
                  }
                  id="email"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="telefono">Numero Telefono</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroTelefono", event.target.value)
                  }
                  id="telefono"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="fiscale">Codice Fiscale</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroFiscale", event.target.value)
                  }
                  id="fiscale"
                  type="text"
                />
              </div>
            </Col>
          </Row>
        </div>
        <div className="row">
          {user.length === 0 ? "Non ci sono dipendenti" : CL}
        </div>
      </Container>
    );
  }
}

AggiornaDipendenti.propTypes = {
  CancellaDipendente: PropTypes.func.isRequired,
  Listadipendenti: PropTypes.func.isRequired,
  registerUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired,
  customerlist: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
  errors: state.errors,
  customerlist: state.customerlist,
});
export default connect(mapStateToProps, {
  Listadipendenti,
  registerUser,
  CancellaDipendente,
})(withRouter(AggiornaDipendenti));
