                </div>
              );  
            default:
              return( 
                <div>
                    <Navbar className="Navbar" style={{display:'block', textAlign: 'center'}}>
                        <Navbar.Brand href="/Login">
                        <img
                        src={Logo}
                        width="50"
                        height="auto"
                        alt="Karm-Logo"
                        className="logonavbar1"
                        />
                        </Navbar.Brand>
                    </Navbar>
                </div>
              );   
          }
    }
 }

 MyNavbar.propTypes = {
    logoutUser: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired,
  };
  
  const mapStateToProps = state => ({
    auth: state.auth,
  });
  
  export default connect(
    mapStateToProps,
    { logoutUser }
  )(MyNavbar);
