import React, { Component } from "react";
import { Link, withRouter } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { registerUser, logoutUser } from "../../actions/authAutentications";
import classnames from "classnames";


//componenti da react-bootstrap
import { Container } from 'react-bootstrap';

//componenti da material-ui


class NuovaPrenotazione extends Component {
  constructor() {
    super();
    this.state = {
      ora_partenza: "",
      partenza: "",
      ora_arrivo: "",
      data_partenza: "",
      data_arrivo: "",
      destinazione: "",
      parcheggio_partenza: "",
      parcheggio_destinazione: "",
      idVeicolo: "",
      idCliente: "",
      idAutista: "",
      errors: {}
    };
  }


  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors
      });
    }
  }

  onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };

  onSubmit = e => {
    e.preventDefault();

    const newPrenotazione = {
        ora_partenza: this.state.ora_partenza,
        partenza: this.state.partenza,
        ora_arrivo: this.state.ora_arrivo,
        data_partenza: this.state.data_partenza,
        data_arrivo: this.state.data_arrivo,
        destinazione: this.state.destinazione,
        parcheggio_partenza: this.state.parcheggio_partenza,
        parcheggio_destinazione: this.state.parcheggio_destinazione,
        idVeicolo: this.state.idVeicolo,
        idCliente: this.state.idCliente,
        idAutista: this.state.idAutista
    };

    this.props.registerPrenotazione(newPrenotazione, this.props.history);
  };
  
  render() {
    const { errors } = this.state;
    
    return (
      <Container style={{marginTop: '3rem', marginBottom: '3rem'}}>
      <div className="Form">
            <form noValidate onSubmit={this.onSubmit}>
            <h3 style={{textAlign: 'center', color: 'black'}}>Nuova Prenotazione</h3>
              <div className="form-group">
              <label htmlFor="partenza">Partenza</label>
                <input
                  onChange={this.onChange}
                  value={this.state.partenza}
                  error={errors.partenza}
                  id="name"
                  type="text"
                  className={classnames("form-control", {
                    invalid: errors.partenza
                  })}
                />
                <span className="red-text">{errors.partenza}</span>
              </div>
              <div className="form-group">
              <label htmlFor="cognome">Destinazione</label>
                <input
                  onChange={this.onChange}
                  value={this.state.cognome}
                  error={errors.cognome}
                  id="cognome"
                  type="text"
                  className={classnames("form-control", {
                    invalid: errors.cognome
                  })}
                />
                <span className="red-text">{errors.cognome}</span>
              </div>
             <div className="form-group">
             <label htmlFor="cognome">Data Partenza</label>
                 <input type="date" />
             </div>
             <div className="form-group">
             <label htmlFor="cognome">Data Arrivo</label>
                 <input type="date" />
             </div>
             <div className="form-group">
                    <label>Tipo Veicolo</label>
                    <select id="sesso" type="text" value={this.state.sesso} 
                    onChange={this.onChange} error={errors.sesso}
                    className={classnames("form-control", {
                      invalid: errors.sesso
                    })}>
                    <option value="" disabled selected>Tipo Veicolo</option>
                    <option value="Uomo">Auto</option>
                    <option value="Donna">Moto</option>
                    <option value="NaN">Bicicletta</option>
                    <option value="NaN">Monopattino</option>
                    </select>
                    <span className="red-text">{errors.sesso}</span>
                </div>
                <div className="form-group">
                     <label htmlFor="cognome">Dispositivo mobile</label>
                 <input type="checkbox" />
             </div>       
              
              <button type="submit" value="Registrazione Log" className="btn btn-dark btn-lg btn-block">Avanti</button>
            </form>
          </div>
          </Container>
    );
  }
}

NuovaPrenotazione.propTypes = {
    logoutUser: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired
  };
  
  const mapStateToProps = state => ({
    auth: state.auth
  });
  
  export default connect(
    mapStateToProps,
    { logoutUser }
  )(NuovaPrenotazione);
