import { Listaclienti } from "../../actions/authAutentications";
import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import {
  GoogleMap,
  Marker,
  InfoWindow,
  useLoadScript,
} from "@react-google-maps/api";
import styleMappa from "./styleMappa";
import Logo from "../../immagini/logo.png";

const libraries = ["places"];
const mapContainerStyle = {
  width: "100vw",
  height: "100vh",
};
const center = {
  lat: 38.115688,
  lng: 13.361267,
};

const options = {
  styles: styleMappa,
  disableDefaultUI: true,
  zoomControl: true,
};

export default function Mappa() {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: "AIzaSyCTbH-HgcbQUWQXz24mm9gmkQCmJk98L7c",
    libraries,
  });
  const [markers, setMarkers] = React.useState([]);
  const [selected, setSelected] = React.useState(null);

  if (loadError) return "Errore nel caricare la mappa";
  if (!isLoaded) return "Mappa in caricamento";

  return (
    <div>
      <h1
        style={{ position: "absolute", zIndex: "100", fontWeight: "semi-bold" }}
      >
        KARM
      </h1>
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        zoom={13}
        center={center}
        options={options}
        onClick={(e) => {
          setMarkers((current) => [
            ...current,
            {
              lat: e.latLng.lat(),
              lng: e.latLng.lng(),
              time: new Date(),
            },
          ]);
        }}
      >
        {markers.map((marker) => (
          <Marker
            key={marker.time.toISOString()}
            position={{ lat: marker.lat, lng: marker.lng }}
            icon={{
              url: "/logo.png",
              scaledSize: new window.google.maps.Size(40, 40),
              origin: new window.google.maps.Point(0, 0),
              anchor: new window.google.maps.Point(20, 20),
            }}
            onClick={() => {
              setSelected(marker);
            }}
          />
        ))}

        {selected ? (
          <InfoWindow position={{ lat: selected.lat, lng: selected.lng }}>
            <div>
              <h2 style={{ color: "#010101" }}>Parcheggio</h2>
              <p style={{ textAlign: "center" }}>Parcheggio karm</p>
            </div>
          </InfoWindow>
        ) : null}
      </GoogleMap>
    </div>
  );
}
