import React, { Component } from "react";
import { Link, withRouter } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { registerUser } from "../../actions/authAutentications";
import classnames from "classnames";

//componenti da react-bootstrap
import { Container } from 'react-bootstrap';

class NuovaPrenotazione extends Component {
  constructor() {
    super();
    this.state = {
      name: "",
      cognome: "",
      cf: "",
      sesso: "",
      luogo_nascita: "",
      provincia_nascita: "",
      patente: "",
      email: "",
      password: "",
      password2: "",
      errors: {}
    };
  }


  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors
      });
    }
  }

  onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };

  onSubmit = e => {
    e.preventDefault();

    const newUser = {
      name: this.state.name,
      cognome: this.state.cognome,
      cf: this.state.cf,
      sesso: this.state.sesso,
      luogo_nascita: this.state.luogo_nascita,
      provincia_nascita: this.state.provincia_nascita,
      patente: this.state.patente,
      email: this.state.email,
      password: this.state.password,
      password2: this.state.password2
    };

    this.props.registerUser(newUser, this.props.history);
  };

  render() {
    const { errors } = this.state;

    return (
      <Container style={{marginTop: '3rem', marginBottom: '3rem'}}>
      <div className="Form">
            <form noValidate onSubmit={this.onSubmit}>
            <h3 style={{textAlign: 'center', color: 'black'}}>Nuova Prenotazione</h3>
              <div className="form-group">
              <label htmlFor="name">Nome</label>
                <input
                  onChange={this.onChange}
                  value={this.state.name}
                  error={errors.name}
                  id="name"
                  type="text"
                  className={classnames("form-control", {
                    invalid: errors.name
                  })}
                />
                <span className="red-text">{errors.name}</span>
              </div>
              <div className="form-group">
              <label htmlFor="cognome">Cognome</label>
                <input
                  onChange={this.onChange}
                  value={this.state.cognome}
                  error={errors.cognome}
                  id="cognome"
                  type="text"
                  className={classnames("form-control", {
                    invalid: errors.cognome
                  })}
                />
                <span className="red-text">{errors.cognome}</span>
              </div>
              <div className="form-group">
              <label htmlFor="cf">Codice Fiscale</label>
                <input
                  onChange={this.onChange}
                  value={this.state.cf}
                  error={errors.cf}
                  id="cf"
                  type="text"
                  className={classnames("form-control", {
                    invalid: errors.cf
                  })}
                />
                <span className="red-text">{errors.cf}</span>
              </div>
              <div className="form-group">
                    <label>Sesso</label>
                    <select id="sesso" type="text" value={this.state.sesso} 
                    onChange={this.onChange} error={errors.sesso}
                    className={classnames("form-control", {
                      invalid: errors.sesso
                    })}>
                    <option value="" disabled selected>Sesso</option>
                    <option value="Uomo">Uomo</option>
                    <option value="Donna">Donna</option>
                    <option value="NaN">Non specifico</option>
                    </select>
                    <span className="red-text">{errors.sesso}</span>
                </div>
                <div className="form-group">
              <label htmlFor="luogo_nascita">Luogo di nascita</label>
                <input
                  onChange={this.onChange}
                  value={this.state.luogo_nascita}
                  error={errors.luogo_nascita}
                  id="luogo_nascita"
                  type="text"
                  className={classnames("form-control", {
                    invalid: errors.luogo_nascita
                  })}
                />
                <span className="red-text">{errors.luogo_nascita}</span>
              </div>
              <div className="form-group">
                    <label>Provincia di nascita</label>
                    <select id="provincia_nascita" type="text" value={this.state.provincia_nascita} 
                    onChange={this.onChange} error={errors.provincia_nascita}
                    className={classnames("form-control", {
                      invalid: errors.provincia_nascita
                    })}>
                    <option value="" disabled selected>Provincia di Nascita</option>
                    <option value="Agrigento">Agrigento</option>
                    <option value="Caltanissetta">Caltanissetta</option>
                    <option value="Catania">Catania</option>
                    <option value="Enna">Enna</option>
                    <option value="Messina">Messina</option>
                    <option value="Palermo">Palermo</option>
                    <option value="Ragusa">Ragusa</option>
                    <option value="Siracusa">Siracusa</option>
                    <option value="Trapani">Trapani</option>
                    </select>
                    <span className="red-text">{errors.provincia_nascita}</span>
                </div>
              <div className="form-group">
              <label htmlFor="patente">Numero Patente</label>
                <input
                  onChange={this.onChange}
                  value={this.state.patente}
                  error={errors.patente}
                  id="patente"
                  type="text"
                  className={classnames("form-control", {
                    invalid: errors.patente
                  })}
                />
                <span className="red-text">{errors.patente}</span>
              </div>
              <div className="form-group">
              <label htmlFor="email">Email</label>
                <input
                  onChange={this.onChange}
                  value={this.state.email}
                  error={errors.email}
                  id="email"
                  type="email"
                  className={classnames("form-control", {
                    invalid: errors.email
                  })}
                />
                <span className="red-text">{errors.email}</span>
              </div>
              <div className="form-group">
              <label htmlFor="password">Password</label>
                <input
                  onChange={this.onChange}
                  value={this.state.password}
                  error={errors.password}
                  id="password"
                  type="password"
                  className={classnames("form-control", {
