import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { Link, withRouter } from "react-router-dom";
import {
  registerPrenotazione,
  ListaPrenotazioni,
} from "../actions/prenotazioniActions";

//componenti da react-bootstrap
import { Container, Col, Row, Modal, Button } from "react-bootstrap";
import "../App.css";
import classnames from "classnames";

import ricercaFiltro from "../utils/ricercaFiltro";

class VisualizzaPrenotazioni extends Component {
  state = {
    filtroNome: "",
    filtroCognome: "",
    filtroEmail: "",
    filtroFiscale: "",
    errors: {},
  };

  async componentDidMount() {
    await this.props.ListaPrenotazioni();
  }
  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors,
      });
    }
  }

  handleChange = (name, value) => {
    this.setState({ [name]: value });
  };

  deleteDipendente(id) {
    this.props.CancellaDipendente(id);
    window.location.reload();
  }

  onChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };

  setModalShow = (input) => {
    this.setState({ modalShow: input });
  };

  render() {
    const { filtroNome, filtroCognome, filtroEmail, filtroFiscale, errors } =
      this.state;
    const { prenotazione } = this.props.listaprenotazioni;
    let prenotazioniFiltrate = [];
    listaprenotazioni = ricercaFiltro(prenotazione, filtroNome, "nome");
    listaprenotazioni = ricercaFiltro(
      listaprenotazioni,
      filtroCognome,
      "cognome"
    );
    listaprenotazioni = ricercaFiltro(listaprenotazioni, filtroEmail, "email");
    listaprenotazioni = ricercaFiltro(
      listaprenotazioni,
      filtroFiscale,
      "codice_fiscale"
    );

    const PRL = (
      <div className="container" style={{ marginBottom: "14rem" }}>
        <div class="center" style={{ textAlign: "left" }}>
          <table class="responsive-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Cognome</th>
                <th>Codice Fiscale</th>
                <th>Email</th>
                <th>Ruolo</th>
                <th>Elimina</th>
              </tr>
            </thead>
            <tbody>
              {listaprenotazioni.map((user) => (
                <tr key={user._id}>
                  <td>{user.nome}</td>
                  <td>{user.cognome}</td>
                  <td>{user.codice_fiscale}</td>
                  <td>{user.email}</td>

                  <td>{user.ruolo}</td>
                  <td>
                    {" "}
                    <button
                      class="btn"
                      onClick={() => this.deleteDipendente(user._id)}
                    >
                      <i class="material-icons">delete</i>
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );

    return (
      <Container fluid style={{ marginTop: "3rem", marginBottom: "3rem" }}>
        <Modal
          {...this.props}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          centered
          show={this.state.modalShow}
          onHide={() => window.location.reload()}
          backdrop="static"
        >
          <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
              Inserisci i dati del dipendente
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Container style={{ marginTop: "3rem", marginBottom: "3rem" }}>
              <div className="Form">
                <form noValidate onSubmit={this.onSubmit}>
                  <div className="form-group">
                    <label>Ruolo</label>
                    <select
                      id="ruolo"
                      type="text"
                      value={this.state.ruolo}
                      onChange={async (e) => {
                        await this.onChange(e);
                        this.setModalShow(true);
                      }}
                      error={errors.ruolo}
                      className={classnames("form-control", {
                        invalid: errors.ruolo,
                      })}
                    >
                      <option value="" disabled selected>
                        Ruolo
                      </option>
                      <option value="Addetto">Addetto al parcheggio</option>
                      <option value="Autista">Autista</option>
                    </select>
                    <span className="red-text">{errors.ruolo}</span>
                  </div>
                </form>
              </div>
            </Container>
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={() => window.location.reload()}>Annulla</Button>
          </Modal.Footer>
        </Modal>
        <div className="Form">
          <Row>
            <Col sm>
              <div className="form-group">
                <label htmlFor="nome">Nome</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroNome", event.target.value)
                  }
                  id="nome"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="cognome">Cognome</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroCognome", event.target.value)
                  }
                  id="cognome"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroEmail", event.target.value)
                  }
                  id="email"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="fiscale">Codice Fiscale</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroFiscale", event.target.value)
                  }
                  id="fiscale"
                  type="text"
                />
              </div>
            </Col>
          </Row>
        </div>
        <div className="row">
          {prenotazione.length === 0 ? "Non ci sono dipendenti" : PRL}
        </div>
      </Container>
    );
  }
}

VisualizzaPrenotazioni.propTypes = {
  CancellaDipendente: PropTypes.func.isRequired,
  Listadipendenti: PropTypes.func.isRequired,
  registerUser: PropTypes.func.isRequired,

  Listaparcheggi: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired,
  customerlist: PropTypes.object.isRequired,
  listaparcheggi: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
  errors: state.errors,
  customerlist: state.customerlist,
  listaparcheggi: state.listaparcheggi,
});
export default connect(mapStateToProps, {
  Listaparcheggi,
  Listadipendenti,
  registerUser,
  CancellaDipendente,
})(withRouter(VisualizzaPrenotazioni));
