import { Listaveicoli } from "../../actions/veicoliActions";
import React, { Component } from "react";

import PropTypes from "prop-types";
import { connect } from "react-redux";
import { Link, withRouter } from "react-router-dom";

import {
  registerVeicolo,
  ModificaVeicolo,
  CancellaVeicolo,
  GetSpecificVeicolo,
} from "../../actions/veicoliActions";

//componenti da react-bootstrap
import { Container, Col, Row, Modal, Button } from "react-bootstrap";

import "../../App.css";
import classnames from "classnames";

import filtriVeicoli from "../../utils/filtriVeicoli";

class AggiornaVeicoli extends Component {
  state = {
    tipo: "",
    modello: "",
    marca: "",
    cilindrata: 0,
    n_posti: 0,
    n_porte: 0,
    targa: "",
    id_parcheggio: "",
    descrizione: "",
    prezzo_festivo: "",
    prezzo_feriale: "",
    stato: "",
    patente_necessaria: "",
    stato: "",
    type_auto: "false",
    type_moto: "false",
    type_bici_mono: "false",
    errors: {},
    filtroTipo: "",
    filtroParcheggio: "",
    filtroID: "",
  };

  async componentDidMount() {
    await this.props.Listaveicoli();
  }
  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors,
      });
    }
  }

  handleChange = (name, value) => {
    this.setState({ [name]: value });
  };
  deleteVeicolo(id) {
    this.props.CancellaVeicolo(id);
    window.location.reload();
  }

  modificaVeicolo(id) {
    this.props.history.push(`veicoli/update/${id}`);
  }

  onChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };

  onSubmit = (e) => {
    e.preventDefault();

    const newVeicolo = {
      tipo: this.state.tipo,
      modello: this.state.modello,
      marca: this.state.marca,
      cilindrata: this.state.cilindrata,
      n_posti: this.state.n_posti,
      n_porte: this.state.n_porte,
      targa: this.state.targa,
      id_parcheggio: this.state.id_parcheggio,
      descrizione: this.state.descrizione,
      prezzo_festivo: this.state.prezzo_festivo,
      prezzo_feriale: this.state.prezzo_feriale,
      stato: this.state.stato,
    };
    this.props.registerVeicolo(newVeicolo, this.props.history);
  };

  setModalShow = (input) => {
    if (this.state.tipo === "") this.setState({ modalShow: input });
    else if (this.state.tipo === "Autovettura")
      this.setState({ modalShow1: input });
    else if (this.state.ruolo === "Moto") this.setState({ modalShow2: input });
    else if (
      this.state.ruolo === "Bicicletta" ||
      this.state.ruolo === "Monopattino"
    )
      this.setState({ modalShow2: input });
  };

  render() {
    const { filtroTipo, filtroParcheggio, filtroID, errors } = this.state;

    let veicoliFiltrati = [];
    const { veicolo } = this.props.listaveicoli;
    veicoliFiltrati = filtriVeicoli(veicolo, filtroTipo, "tipo");
    veicoliFiltrati = filtriVeicoli(
      veicoliFiltrati,
      filtroParcheggio,
      "id_parcehggio"
    );
    veicoliFiltrati = filtriVeicoli(veicoliFiltrati, filtroID, "_id");

    const VL = (
      <div className="container">
        <div class="center" style={{ textAlign: "left" }}>
          <table class="responsive-table">
            <thead>
              <tr>
                <th>Tipo</th>
                <th>Modello</th>
                <th>Marca</th>
                <th>Cilindrata</th>
                <th>Numero posti</th>
                <th>Numero porte</th>
                <th>Targa</th>
                <th>Parcheggio</th>
                <th>Prezzo Festivo</th>
                <th>Prezzo Feriale</th>
                <th>Stato</th>
                <th>Modifica</th>
              </tr>
            </thead>
            <tbody>
              {veicoliFiltrati.map((veicolo) => (
                <tr key={veicolo._id}>
                  <td>{veicolo.tipo}</td>
                  <td>{veicolo.modello}</td>
                  <td>{veicolo.marca}</td>
                  <td>{veicolo.cilindrata}</td>
                  <td>{veicolo.n_posti}</td>
                  <td>{veicolo.n_porte}</td>
                  <td>{veicolo.targa}</td>
                  <td>{veicolo.id_parcheggio}</td>
                  <td>{veicolo.prezzo_festivo}</td>
                  <td>{veicolo.prezzo_feriale}</td>
                  <td>{veicolo.stato}</td>
                  <td>
                    {" "}
                    <button
                      class="btn"
                      onClick={() => this.modificaVeicolo(veicolo._id)}
                    >
                      <i class="material-icons">create</i>
                    </button>
                    <button
                      class="btn"
                      onClick={() => this.deleteVeicolo(veicolo._id)}
                    >
                      <i class="material-icons">delete</i>
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );

    return (
      <Container fluid style={{ marginTop: "3rem", marginBottom: "3rem" }}>
        <Modal
          {...this.props}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          centered
          show={this.state.modalShow}
          onHide={() => window.location.reload()}
          backdrop="static"
        >
          <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
              Inserisci i dati del veicolo
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Container style={{ marginTop: "3rem", marginBottom: "3rem" }}>
              <div className="Form">
                <form noValidate onSubmit={this.onSubmit}>
                  <div className="form-group">
                    <label>Tipo</label>
                    <select
                      id="tipo"
                      type="text"
                      value={this.state.tipo}
                      onChange={async (e) => {
                        await this.onChange(e);
                        this.setModalShow(true);
                      }}
                      error={errors.tipo}
                      className={classnames("form-control", {
                        invalid: errors.tipo,
                      })}
                    >
                      <option value="" disabled selected>
                        Tipo
                      </option>
                      <option value="Addetto">Addetto al parcheggio</option>
                      <option value="Autista">Autista</option>
                    </select>
                    <span className="red-text">{errors.tipo}</span>
                  </div>
                </form>
              </div>
            </Container>
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={() => window.location.reload()}>Annulla</Button>
          </Modal.Footer>
        </Modal>
        <Container fluid style={{ marginTop: "3rem", marginBottom: "3rem" }}>
          <Modal
            {...this.props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
            show={this.state.modalShow2}
            onHide={() => this.setModalShow(false)}
            backdrop="static"
          >
            <Modal.Header closeButton>
              <Modal.Title id="contained-modal-title-vcenter">
                Inserisci i dati dell'autista
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <Container style={{ marginTop: "3rem", marginBottom: "3rem" }}>
                <div className="Form">
                  <form noValidate onSubmit={this.onSubmit}>
                    <div className="form-group">
                      <label htmlFor="nome">Nome</label>
                      <input
                        onChange={this.onChange}
                        value={this.state.nome}
                        error={errors.nome}
                        id="nome"
                        type="text"
                        className={classnames("form-control", {
                          invalid: errors.nome,
                        })}
                      />
                      <span className="red-text">{errors.nome}</span>
                    </div>
                    <div className="form-group">
                      <label htmlFor="cognome">Cognome</label>
                      <input
                        onChange={this.onChange}
                        value={this.state.cognome}
                        error={errors.cognome}
                        id="cognome"
                        type="text"
                        className={classnames("form-control", {
                          invalid: errors.cognome,
                        })}
                      />
                      <span className="red-text">{errors.cognome}</span>
                    </div>
                    <div className="form-group">
                      <label htmlFor="data_nascita">Data di nascita</label>
                      <input
                        onChange={this.onChange}
                        value={this.state.data_nascita}
                        error={errors.data_nascita}
                        id="data_nascita"
                        type="date"
                        className={classnames("form-control", {
                          invalid: errors.data_nascita,
                        })}
                      />
                      <span className="red-text">{errors.data_nascita}</span>
                    </div>
                    <div className="form-group">
                      <label>Sesso</label>
                      <select
                        id="sesso"
                        type="text"
                        value={this.state.sesso}
                        onChange={this.onChange}
                        error={errors.sesso}
                        className={classnames("form-control", {
                          invalid: errors.sesso,
                        })}
                      >
                        <option value="" disabled selected>
                          Sesso
                        </option>
                        <option value="M">Uomo</option>
                        <option value="F">Donna</option>
                      </select>
                      <span className="red-text">{errors.sesso}</span>
                    </div>
                    <div className="form-group">
                      <label htmlFor="luogo_nascita">Luogo di nascita</label>
                      <input
                        onChange={this.onChange}
                        value={this.state.luogo_nascita}
                        error={errors.luogo_nascita}
                        id="luogo_nascita"
                        type="text"
                        className={classnames("form-control", {
                          invalid: errors.luogo_nascita,
                        })}
                      />
                      <span className="red-text">{errors.luogo_nascita}</span>
                    </div>
                    <div className="form-group">
                      <label>Provincia di nascita</label>
                      <select
                        id="provincia_nascita"
                        type="text"
                        value={this.state.provincia_nascita}
                        onChange={this.onChange}
                        error={errors.provincia_nascita}
                        className={classnames("form-control", {
                          invalid: errors.provincia_nascita,
                        })}
                      >
                        <option value="" disabled selected>
                          Provincia di Nascita
                        </option>
                        <option value="AG">Agrigento</option>
                        <option value="CL">Caltanissetta</option>
                        <option value="CT">Catania</option>
                        <option value="EN">Enna</option>
                        <option value="ME">Messina</option>
                        <option value="PA">Palermo</option>
                        <option value="RG">Ragusa</option>
                        <option value="SR">Siracusa</option>
                        <option value="TP">Trapani</option>
                      </select>
                      <span className="red-text">
                        {errors.provincia_nascita}
                      </span>
                    </div>
                    <div className="form-group">
                      <label htmlFor="codice_fiscale">Codice Fiscale</label>
                      <input
                        onChange={this.onChange}
                        value={this.state.codice_fiscale}
                        error={errors.codice_fiscale}
                        id="codice_fiscale"
                        type="text"
                        className={classnames("form-control", {
                          invalid: errors.codice_fiscale,
                        })}
                      />
                      <span className="red-text">{errors.codice_fiscale}</span>
                    </div>
                    <div className="form-group">
                      <label htmlFor="numero_patente">Numero Patente</label>
                      <input
                        onChange={this.onChange}
                        value={this.state.numero_patente}
                        error={errors.numero_patente}
                        id="numero_patente"
                        type="text"
                        className={classnames("form-control", {
                          invalid: errors.numero_patente,
                        })}
                      />
                      <span className="red-text">{errors.numero_patente}</span>
                    </div>
                    <div className="form-group">
                      <label htmlFor="data_rilascio_patente">
                        Data rilascio
                      </label>
                      <input
                        type="date"
                        onChange={this.onChange}
                        value={this.state.data_rilascio_patente}
                        error={errors.data_rilascio_patente}
                        id="data_rilascio_patente"
                        className={classnames("form-control", {
                          invalid: errors.data_rilascio_patente,
                        })}
                      />
                      <span className="red-text">
                        {errors.data_rilascio_patente}
                      </span>
                    </div>
                    <div className="form-group">
                      <label htmlFor="data_scadenza_patente">
                        Data scadenza
                      </label>
                      <input
                        onChange={this.onChange}
                        type="date"
                        value={this.state.data_scadenza_patente}
                        error={errors.data_scadenza_patente}
                        id="data_scadenza_patente"
                        className={classnames("form-control", {
                          invalid: errors.data_scadenza_patente,
                        })}
                      />
                      <span className="red-text">
                        {errors.data_scadenza_patente}
                      </span>
                    </div>
                    <div className="form-group">
                      <label htmlFor="ente_rilascio">Ente rilascio</label>
                      <input
                        onChange={this.onChange}
                        placeholder={user.ente_rilascio}
                        value={this.state.ente_rilascio}
                        error={errors.ente_rilascio}
                        id="ente_rilascio"
                        type="text"
                        className={classnames("form-control", {
                          invalid: errors.ente_rilascio,
                        })}
                      />
                      <span className="red-text">{errors.ente_rilascio}</span>
                    </div>

                    <div className="form-group">
                      <label htmlFor="email">Email</label>
                      <input
                        onChange={this.onChange}
                        value={this.state.email}
                        error={errors.email}
                        id="email"
                        type="email"
                        className={classnames("form-control", {
                          invalid: errors.email,
                        })}
                      />
                      <span className="red-text">{errors.email}</span>
                    </div>
                    <div className="form-group">
                      <label htmlFor="password">Password</label>
                      <input
                        onChange={this.onChange}
                        value={this.state.password}
                        error={errors.password}
                        id="password"
                        type="password"
                        className={classnames("form-control", {
                          invalid: errors.password,
                        })}
                      />
                      <span className="red-text">{errors.password}</span>
                    </div>
                    <div className="form-group">
                      <label htmlFor="password2">Conferma Password</label>
                      <input
                        onChange={this.onChange}
                        value={this.state.password2}
                        error={errors.password2}
                        id="password2"
                        type="password"
                        className={classnames("form-control", {
                          invalid: errors.password2,
                        })}
                      />
                      <span className="red-text">{errors.password2}</span>
                    </div>
                    <button
                      type="submit"
                      value="Registrazione Log"
                      className="btn btn-dark btn-lg btn-block bottone"
                    >
                      Inserisci
                    </button>
                  </form>
                </div>
              </Container>
            </Modal.Body>
            <Modal.Footer>
              <Button onClick={() => this.setModalShow(false)}>Annulla</Button>
            </Modal.Footer>
          </Modal>
        </Container>
        <Container fluid style={{ marginTop: "3rem", marginBottom: "3rem" }}>
          <Modal
            {...this.props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
            show={this.state.modalShow1}
            onHide={() => this.setModalShow(false)}
            backdrop="static"
          >
            <Modal.Header closeButton>
              <Modal.Title id="contained-modal-title-vcenter">
                Inserisci i dati dell'addetto al parcheggio
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <Container style={{ marginTop: "3rem", marginBottom: "3rem" }}>
                <div className="Form">
                  <form noValidate onSubmit={this.onSubmit}>
                    <div className="form-group">
                      <label htmlFor="nome">Nome</label>
                      <input
                        onChange={this.onChange}
                        value={this.state.nome}
                        error={errors.nome}
                        id="nome"
                        type="text"
                        className={classnames("form-control", {
                          invalid: errors.nome,
                        })}
                      />
                      <span className="red-text">{errors.nome}</span>
                    </div>
                    <div className="form-group">
                      <label htmlFor="cognome">Cognome</label>
                      <input
                        onChange={this.onChange}
                        value={this.state.cognome}
                        error={errors.cognome}
                        id="cognome"
                        type="text"
                        className={classnames("form-control", {
                          invalid: errors.cognome,
                        })}
                      />
                      <span className="red-text">{errors.cognome}</span>
                    </div>
                    <div className="form-group">
                      <label htmlFor="data_nascita">Data di nascita</label>
                      <input
                        onChange={this.onChange}
                        value={this.state.data_nascita}
                        error={errors.data_nascita}
                        id="data_nascita"
                        type="date"
                        className={classnames("form-control", {
                          invalid: errors.data_nascita,
                        })}
                      />
                      <span className="red-text">{errors.data_nascita}</span>
                    </div>
                    <div className="form-group">
                      <label>Sesso</label>
                      <select
                        id="sesso"
                        type="text"
                        value={this.state.sesso}
                        onChange={this.onChange}
                        error={errors.sesso}
                        className={classnames("form-control", {
                          invalid: errors.sesso,
                        })}
                      >
                        <option value="" disabled selected>
                          Sesso
                        </option>
                        <option value="M">Uomo</option>
                        <option value="F">Donna</option>
                      </select>
                      <span className="red-text">{errors.sesso}</span>
                    </div>
                    <div className="form-group">
                      <label htmlFor="luogo_nascita">Luogo di nascita</label>
                      <input
                        onChange={this.onChange}
                        value={this.state.luogo_nascita}
                        error={errors.luogo_nascita}
                        id="luogo_nascita"
                        type="text"
                        className={classnames("form-control", {
                          invalid: errors.luogo_nascita,
                        })}
                      />
                      <span className="red-text">{errors.luogo_nascita}</span>
                    </div>
                    <div className="form-group">
                      <label>Provincia di nascita</label>
                      <select
                        id="provincia_nascita"
                        type="text"
                        value={this.state.provincia_nascita}
                        onChange={this.onChange}
                        error={errors.provincia_nascita}
                        className={classnames("form-control", {
                          invalid: errors.provincia_nascita,
                        })}
                      >
                        <option value="" disabled selected>
                          Provincia di Nascita
                        </option>
                        <option value="AG">Agrigento</option>
                        <option value="CL">Caltanissetta</option>
                        <option value="CT">Catania</option>
                        <option value="EN">Enna</option>
                        <option value="ME">Messina</option>
                        <option value="PA">Palermo</option>
                        <option value="RG">Ragusa</option>
                        <option value="SR">Siracusa</option>
                        <option value="TP">Trapani</option>
                      </select>
                      <span className="red-text">
                        {errors.provincia_nascita}
                      </span>
                    </div>
                    <div className="form-group">
                      <label htmlFor="codice_fiscale">Codice Fiscale</label>
                      <input
                        onChange={this.onChange}
                        value={this.state.codice_fiscale}
                        error={errors.codice_fiscale}
                        id="codice_fiscale"
                        type="text"
                        className={classnames("form-control", {
                          invalid: errors.codice_fiscale,
                        })}
                      />
                      <span className="red-text">{errors.codice_fiscale}</span>
                    </div>
                    <div className="form-group">
                      <label htmlFor="email">Email</label>
                      <input
                        onChange={this.onChange}
                        value={this.state.email}
                        error={errors.email}
                        id="email"
                        type="email"
                        className={classnames("form-control", {
                          invalid: errors.email,
                        })}
                      />
                      <span className="red-text">{errors.email}</span>
                    </div>
                    <div className="form-group">
                      <label htmlFor="password">Password</label>
                      <input
                        onChange={this.onChange}
                        value={this.state.password}
                        error={errors.password}
                        id="password"
                        type="password"
                        className={classnames("form-control", {
                          invalid: errors.password,
                        })}
                      />
                      <span className="red-text">{errors.password}</span>
                    </div>
                    <div className="form-group">
                      <label htmlFor="password2">Conferma Password</label>
                      <input
                        onChange={this.onChange}
                        value={this.state.password2}
                        error={errors.password2}
                        id="password2"
                        type="password"
                        className={classnames("form-control", {
                          invalid: errors.password2,
                        })}
                      />
                      <span className="red-text">{errors.password2}</span>
                    </div>
                    <button
                      type="submit"
                      value="Registrazione Log"
                      className="btn btn-dark btn-lg btn-block bottone"
                    >
                      Inserisci
                    </button>
                  </form>
                </div>
              </Container>
            </Modal.Body>
            <Modal.Footer>
              <Button onClick={() => this.setModalShow(false)}>Annulla</Button>
            </Modal.Footer>
          </Modal>
        </Container>
        <div className="Form">
          <Row>
            <Col sm={3} md={{ span: 3, offset: 9 }}>
              <button
                className="btn btn-dark btn-lg btn-block bottone"
                onClick={() => this.setModalShow(true)}
              >
                + Inserisci dipendente
              </button>
            </Col>
          </Row>
          <Row>
            <Col sm>
              <div className="form-group">
                <label htmlFor="nome">Nome</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroNome", event.target.value)
                  }
                  id="nome"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="cognome">Cognome</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroCognome", event.target.value)
                  }
                  id="cognome"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroEmail", event.target.value)
                  }
                  id="email"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="fiscale">Codice Fiscale</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroFiscale", event.target.value)
                  }
                  id="fiscale"
                  type="text"
                />
              </div>
            </Col>
          </Row>
        </div>
        <div className="row">
          {user.length === 0 ? "Non ci sono veicoli" : VL}
        </div>
      </Container>
    );
  }
}

AggiornaVeicoli.propTypes = {
  CancellaVeicolo: PropTypes.func.isRequired,
  registerVeicolo: PropTypes.func.isRequired,
  Listaveicoli: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired,
  listaveicoli: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
  errors: state.errors,
  listaveicoli: state.listaveicoli,
});
export default connect(mapStateToProps, {
  registerVeicolo,
  Listaveicoli,
  CancellaVeicolo,
})(AggiornaVeicoli);
