import React, { Component } from "react";
import '../../App.css';
import car from '../../immagini/CAR.png';
import { Container, Row, Col, Button } from 'react-bootstrap';


export default class Chisiamo extends Component {

  constructor(props) {
    super(props);
  }




   render(){ 
      return( 
         <div className='HomeCliente'> 
            <Container>
  <Row>
    <Col sm={6}>
      <h1 style={{fontWeight: 'bold'}} className="TestoHomeCliente">Il passaggio di cui <br />hai bisogno <br />e molto altro<h4 style={{fontWeight: '300'}} >Richiedi una corsa, sali sul veicolo e goditi il viaggio.</h4></h1>
    </Col>
    <Col sm={6}><img src={car} alt="fiat-500" className='Fiat'/></Col>
  </Row>
        <br /> <br /> <br /> <br />
        <Row>
    <Col sm></Col>
    <Col sm style={{alignItems: 'center'}}><Col sm></Col><Col sm></Col><Col sm><Button href="/NuovaPrenotazione"className = "bottone" variant="outline-primary">Prenota</Button>{' '}</Col>
    </Col>
    <Col sm style={{alignItems: 'center'}}><Col sm></Col><Col sm><Button className = "bottone" variant="outline-primary">Gestione Corsa</Button>{' '}</Col><Col sm></Col>
    </Col>
    <Col sm></Col>
  </Row> <br /><br /><br /><br />
  <Row>
    <Col sm={12}>
      <h2 style={{fontWeight: 'bolder', textAlign: 'center'}}> TROVA IL MEZZO PER ANDARE DOVE VUOI </h2>
    </Col>
  </Row>
  <br /> <br />
  <Row>
    <Col sm style={{textAlign: 'center', marginBottom: '2rem'}}><ScheduleIcon style={{ fontSize: 60, color: "white" }}/><br/><br/><h5 style={{fontWeight: "bolder"}}>PARTI QUANDO VUOI</h5><h6>Richiedi una corsa a qualsiasi ora e in qualsiasi giorno dell'anno.</h6></Col>
    <Col sm style={{textAlign: 'center', marginBottom: '2rem'}}><LocalAtmIcon style={{ fontSize: 60, color: "white" }}/><br/><br/><h5 style={{fontWeight: "bolder"}}>OPZIONI A PREZZI ACCESSIBILI</h5><h6>Confronta i prezzi di tutte le opzioni di corsa, dai tragitti da pendolare giornalieri alle serate speciali.</h6></Col>
    <Col sm style={{textAlign: 'center', marginBottom: '2rem'}}><MapIcon style={{ fontSize: 60, color: "white" }}/><br/><br/><h5 style={{fontWeight: "bolder"}}>UN MODO FACILE PER SPOSTARSI</h5><h6>Tocca per farti accompagnare a destinazione dall’autista o guida tu e divertiti</h6></Col>
  </Row>
</Container>  

         </div>
      ); 
   } 
}
