import { Listadipendenti } from "../../actions/authAutentications";
import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";

//componenti da react-bootstrap
import { Container, Col, Row, Modal, Button } from "react-bootstrap";
import "../../App.css";
import classnames from "classnames";

import ricercaFiltro from "../../utils/ricercaFiltro";

class AggiornaDipendenti extends Component {
  state = {
    name: "",
    cognome: "",
    cf: "",
    sesso: "",
    luogo_nascita: "",
    provincia_nascita: "",
    patente: "",
    email: "",
    password: "",
    numero_telefono: "",
    ruolo: "",
    filtroNome: "",
    filtroCognome: "",
    filtroEmail: "",
    filtroTelefono: "",
    filtroFiscale: "",
  };

  handleChange = (name, value) => {
    this.setState({ [name]: value });
  };

  onChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };

  onSubmit = (e) => {
    e.preventDefault();

    const newUser = {
      name: this.state.name,
      cognome: this.state.cognome,
      cf: this.state.cf,
      sesso: this.state.sesso,
      luogo_nascita: this.state.luogo_nascita,
      provincia_nascita: this.state.provincia_nascita,
      patente: this.state.patente,
      email: this.state.email,
      password: this.state.password,
      numero_telefono: this.state.numero_telefono,
      ruolo: this.state.ruolo,
    };
    this.props.registerUser(newUser, this.props.history);
    this.setModalShow(false);
  };

  async componentDidMount() {
    await this.props.Listadipendenti();
  }

  viewCustomer(id) {
    console.log(id);
  }

  render() {
    const {
      filtroNome,
      filtroCognome,
      filtroEmail,
      filtroTelefono,
      filtroFiscale,
      errors,
    } = this.state;

    let utentiFiltrati = [];
    const { user } = this.props.customerlist;
    utentiFiltrati = ricercaFiltro(user, filtroNome, "name");
    utentiFiltrati = ricercaFiltro(utentiFiltrati, filtroCognome, "cognome");
    utentiFiltrati = ricercaFiltro(utentiFiltrati, filtroEmail, "email");
    utentiFiltrati = ricercaFiltro(
      utentiFiltrati,
      filtroTelefono,
      "numero_telefono"
    );
    utentiFiltrati = ricercaFiltro(utentiFiltrati, filtroFiscale, "cf");

    const CL = (
      <div className="container">
        <div class="center" style={{ textAlign: "left" }}>
          <table class="responsive-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Cognome</th>
                <th>Email</th>
                <th>Codice Fiscale</th>
                <th>Numero di Telefono</th>
              </tr>
            </thead>
            <tbody>
              {utentiFiltrati.map((user) => (
                <tr key={user._id}>
                  <td>{user.name}</td>
                  <td>{user.cognome}</td>
                  <td>{user.email}</td>
                  <td>{user.cf}</td>
                  <td>{user.numero_telefono}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );

    return (
      <Container fluid style={{ marginTop: "3rem", marginBottom: "3rem" }}>
        <Modal
          {...this.props}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          centered
          show={this.state.modalShow}
          onHide={() => this.setModalShow(false)}
          backdrop="static"
        >
          <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
              Inserisci i dati del veicolo
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Container>
              <div className="Form">
                <form noValidate onSubmit={this.onSubmit}>
                  <div className="form-group">
                    <label>Tipo</label>
                    <select
                      id="tipo"
                      type="text"
                      value={this.state.tipo}
                      error={errors.tipo}
                      onChange={this.onChange}
                      className={classnames("form-control", {
                        invalid: errors.tipo,
                      })}
                    >
                      <option value="" disabled selected>
                        Tipo
                      </option>
                      <option value="Auto">Auto</option>
                      <option value="Moto">Moto</option>
                      <option value="Bicicletta">Bicicletta</option>
                      <option value="Monopattino">Monopattino</option>
                    </select>
                    <span className="red-text">{errors.tipo}</span>
                  </div>
                  <div className="form-group">
                    <label htmlFor="modello">Modello</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.modello}
                      id="modello"
                      error={errors.modello}
                      type="text"
                      className={classnames("form-control", {
                        invalid: errors.modello,
                      })}
                    />
                    <span className="red-text">{errors.modello}</span>
                  </div>
                  <div className="form-group">
                    <label htmlFor="cilindrata">Cilindrata</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.cilindrata}
                      error={errors.cilindrata}
                      id="cilindrata"
                      type="number"
                      className={classnames("form-control", {
                        invalid: errors.cilindrata,
                      })}
                    />
                    <span className="red-text">{errors.cilindrata}</span>
                  </div>
                  <div className="form-group">
                    <label>Patente Necessaria</label>
                    <select
                      id="patente_necessaria"
                      type="text"
                      value={this.state.patente_necessaria}
                      onChange={this.onChange}
                      error={errors.patente_necessaria}
                      className={classnames("form-control", {
                        invalid: errors.patente_necessaria,
                      })}
                    >
                      <option value="" disabled selected>
                        Patente
                      </option>
                      <option value="AM">AM</option>
                      <option value="A1">A1</option>
                      <option value="A2">A2</option>
                      <option value="B">B</option>
                      <option value="C">C</option>
                      <option value="NaN">Nessuna</option>
                    </select>
                    <span className="red-text">
                      {errors.patente_necessaria}
                    </span>
                  </div>
                  <button
                    type="submit"
                    value="Aggiunta Veicolo"
                    className="btn btn-dark btn-lg btn-block bottone"
                  >
                    Inserisci
                  </button>
                </form>
              </div>
            </Container>
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={() => this.setModalShow(false)}>Annulla</Button>
          </Modal.Footer>
        </Modal>
        <div className="Form">
          <Row>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm>
              <button
                className="btn btn-dark btn-lg btn-block bottone"
                onClick={() => this.setModalShow(true)}
              >
                + Inserisci Veicolo
              </button>
            </Col>
          </Row>
          <Row>
            <Col sm>
              <div className="form-group">
                <label htmlFor="tipo">Tipo</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroTipo", event.target.value)
                  }
                  id="tipo"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="modello">Modello</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroModello", event.target.value)
                  }
                  id="modello"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="cilindrata">Cilindrata</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroCilindrata", event.target.value)
                  }
                  id="cilindrata"
                  type="text"
                />
              </div>
            </Col>
          </Row>
        </div>
        <div className="row">
          {veicolo.length === 0 ? "Non ci sono veicoli" : CL}
        </div>
      </Container>
    );
  }
}

AggiornaDipendenti.propTypes = {
  Listadipendenti: PropTypes.func.isRequired,

  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired,
  customerlist: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
  errors: state.errors,
  customerlist: state.customerlist,
});
export default connect(mapStateToProps, { Listadipendenti })(
  AggiornaDipendenti
);
