import React, { Component } from "react";
import Car from '../../immagini/CAR.png';
import '../../App.css';
import AccessTimeIcon from '@material-ui/icons/AccessTime';
import { green } from '@material-ui/core/colors';
import MonetizationOnIcon from '@material-ui/icons/MonetizationOn';
import MapIcon from '@material-ui/icons/Map';
import PropTypes from "prop-types";

class HomeCliente extends Component {

   render(){ 
    const { user } = this.props.auth;
      return( 
         <div className='home'> 
            <div>
            <h1>Benvenuto, <b>Hey there,</b> {user.name.split(" ")[0]}</h1>
            <h1 className='testo-auto'>Il passaggio di cui <br />hai bisogno<br /> e molto altro</h1>
            <h3 className='testo-auto2'>Richiedi una corsa, sali sul veicolo e goditi il viaggio.</h3>
            <img className='car' src={Car} alt="fiat-500" />
            <div id='main-home'>
            <button className='bottone' type="button"
    onClick={(e) => {
      e.preventDefault();
      window.location.href='/FormPrenotazione';
      }}
><h2 className='tastoPrenota'>Prenota</h2></button>  <button className='bottone'><h2 className='tastoGestione'>Gestione Corsa</h2></button>
            </div>
            </div>
            <div id='skills-section'>
               <div class="container">
               <h2>TROVA IL MEZZO PER ANDARE DOVE VUOI</h2><br />
                  <div id='skills'>
                  <div class="skill">
                     <AccessTimeIcon style={{ fontSize: 50, color: green[50] }} /> 
                     <h4>PARTI QUANDO VUOI</h4><br />
                     <p>Richiedi una corsa a qualsiasi ora<br /> e in qualsiasi giorno dell'anno.</p>
                  </div>
                  <div class="skill">
                     <MonetizationOnIcon style={{ fontSize: 50, color: green[50] }}/> 
                     <h4>OPZIONI A PREZZI ACCESSIBILI</h4><br />
                     <p>Confronta i prezzi di tutte le opzioni 
di corsa, dai tragitti da pendolare 
giornalieri alle serate speciali.</p>
                  </div>
                  <div class="skill">
                  <MapIcon style={{ fontSize: 50, color: green[50] }}/> 
                     <h4>UN MODO SEMPLICE PER SPOSTARSI IN CITTA'</h4><br />
                     <p>Prenota per farti accompagnare a 
destinazione <br /> dall’autista 
o guida tu e divertiti</p>
                  </div>
                  </div>
               </div>
            </div>
         </div>
      ); 
   } 
}
