import { Listadipendenti } from "../../actions/authAutentications";
import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";

//componenti da react-bootstrap
import { Container, Col, Row } from "react-bootstrap";

import ricercaFiltro from "../../utils/ricercaFiltro";

class AggiornaDipendenti extends Component {
  state = {
    name: "",
    cognome: "",
    cf: "",
    sesso: "",
    luogo_nascita: "",
    provincia_nascita: "",
    patente: "",
    email: "",
    password: "",
    numero_telefono: "",
    ruolo: "",
    filtroNome: "",
    filtroCognome: "",
    filtroEmail: "",
    filtroTelefono: "",
    filtroFiscale: "",
  };

  handleChange = (name, value) => {
    this.setState({ [name]: value });
  };

  onChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };

  onSubmit = (e) => {
    e.preventDefault();

    const newUser = {
      name: this.state.name,
      cognome: this.state.cognome,
      cf: this.state.cf,
      sesso: this.state.sesso,
      luogo_nascita: this.state.luogo_nascita,
      provincia_nascita: this.state.provincia_nascita,
      patente: this.state.patente,
      email: this.state.email,
      password: this.state.password,
      numero_telefono: this.state.numero_telefono,
      ruolo: this.state.ruolo,
    };
    this.props.registerUser(newUser, this.props.history);
    this.setModalShow(false);
  };

  async componentDidMount() {
    await this.props.Listadipendenti();
  }

  viewCustomer(id) {
    console.log(id);
  }

  render() {
    const {
      filtroNome,
      filtroCognome,
      filtroEmail,
      filtroTelefono,
      filtroFiscale,
    } = this.state;

    let utentiFiltrati = [];
    const { user } = this.props.customerlist;
    utentiFiltrati = ricercaFiltro(user, filtroNome, "name");
    utentiFiltrati = ricercaFiltro(utentiFiltrati, filtroCognome, "cognome");
    utentiFiltrati = ricercaFiltro(utentiFiltrati, filtroEmail, "email");
    utentiFiltrati = ricercaFiltro(
      utentiFiltrati,
      filtroTelefono,
      "numero_telefono"
    );
    utentiFiltrati = ricercaFiltro(utentiFiltrati, filtroFiscale, "cf");

    const CL = (
      <div className="container">
        <div class="center" style={{ textAlign: "left" }}>
          <table class="responsive-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Cognome</th>
                <th>Email</th>
                <th>Codice Fiscale</th>
                <th>Numero di Telefono</th>
              </tr>
            </thead>
            <tbody>
              {utentiFiltrati.map((user) => (
                <tr key={user._id}>
                  <td>{user.name}</td>
                  <td>{user.cognome}</td>
                  <td>{user.email}</td>
                  <td>{user.cf}</td>
                  <td>{user.numero_telefono}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );

    return (
      <Container fluid style={{ marginTop: "3rem", marginBottom: "3rem" }}>
        <div className="Form">
          <Row>
            <Col sm>
              <div className="form-group">
                <label htmlFor="name">Nome</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroNome", event.target.value)
                  }
                  id="name"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="cognome">Cognome</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroCognome", event.target.value)
                  }
                  id="cognome"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroEmail", event.target.value)
                  }
                  id="email"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="numero">Numero di telefono</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroTelefono", event.target.value)
                  }
                  id="numero"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="cf">Codice Fiscale</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroFiscale", event.target.value)
                  }
                  id="cf"
                  type="text"
                />
              </div>
            </Col>
          </Row>
        </div>
        <div className="row">
          {user.length === 0 ? "Non ci sono dipendenti" : CL}
        </div>
      </Container>
    );
  }
}

AggiornaDipendenti.propTypes = {
  Listadipendenti: PropTypes.func.isRequired,

  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired,
  customerlist: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
  errors: state.errors,
  customerlist: state.customerlist,
});
export default connect(mapStateToProps, { Listadipendenti })(
  AggiornaDipendenti
);
