import React, { Component } from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { loginUser, recuperoPassword } from "../actions/authAutentications";
import classnames from "classnames";
import axios from "axios";

//componenti da react-bootstrap
import { Container, Row, Col } from "react-bootstrap";

class Email extends Component {
  constructor() {
    super();
    this.state = {
      email: "",
      password: "",
      modalShow: false,
      errors: {},
    };
  }

  setModalShow = (input) => {
    this.setState({ modalShow: input });
  };

  handleChange = (name, value) => {
    this.setState({ [name]: value });
  };

  onChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };

  onSubmit = (e) => {
    e.preventDefault();

    const userData = {
      email: this.state.email,
      password: this.state.password,
    };

    this.props.recuperoPassword(userData);
    this.props.history.push("/codiceOTP");
  };

  render() {
    const { errors } = this.state;

    return (
      <Container style={{ marginTop: "10rem", marginBottom: "17rem" }}>
        <div className="form">
          <form noValidate onSubmit={this.onSubmit}>
            <h3 style={{ textAlign: "center", color: "black" }}>
              Inserisci l'email
            </h3>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                onChange={this.onChange}
                value={this.state.email}
                error={errors.email}
                id="email"
                type="email"
                className={classnames("form-control", {
                  invalid: errors.email || errors.emailnotfound,
                })}
              />
              <span className="red-text">
                {errors.email}
                {errors.emailnotfound}
              </span>
            </div>

            <button
              type="submit"
              value="Registrazione Log"
              className="btn btn-dark btn-lg btn-block bottone"
            >
              Avanti
            </button>
            <h5 style={{ marginTop: "1rem", color: "black" }}>
              <p className="forgot-password text-right">
                Non hai un account? <Link to="/register">Registrati</Link>
              </p>
            </h5>
          </form>
        </div>
      </Container>
    );
  }
}

Email.propTypes = {
  loginUser: PropTypes.func.isRequired,
  recuperoPassword: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
  errors: state.errors,
});

export default connect(mapStateToProps, { loginUser, recuperoPassword })(Email);
