import { Listaparcheggi } from "../../actions/parcheggiActions";
import React, { Component } from "react";

import PropTypes from "prop-types";
import { connect } from "react-redux";
import { Link, withRouter } from "react-router-dom";

import { registerParcheggio } from "../../actions/parcheggiActions";

//componenti da react-bootstrap
import { Container, Col, Row, Modal, Button } from "react-bootstrap";

import "../../App.css";
import classnames from "classnames";

import filtriParcheggi from "../../utils/filtriParcheggi";

class AggiornaParcheggi extends Component {
  state = {
    nome: "",
    indirizzo: "",
    numero_civico: "",
    capienza_auto: "",
    capienza_moto: "",
    capienza_bici: "",
    capienza_monopattino: "",
    errors: {},
    filtroNome: "",
    filtroIndirizzo: "",
    modalShow: false,
  };

  setModalShow = (input) => {
    this.setState({ modalShow: input });
  };

  handleChange = (name, value) => {
    this.setState({ [name]: value });
  };

  async componentDidMount() {
    await this.props.Listaparcheggi();
  }

  viewCustomer(id) {
    console.log(id);
  }

  onChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };

  onSubmit = (e) => {
    e.preventDefault();

    const newParcheggio = {
      nome: this.state.nome,
      indirizzo: this.state.indirizzo,
      numero_civico: this.state.numero_civico,
      capienza_auto: this.state.capienza_auto,
      capienza_moto: this.state.capienza_moto,
      capienza_bici: this.state.capienza_bici,
      capienza_monopattino: this.state.capienza_monopattino,
    };
    this.props.registerParcheggio(newParcheggio, this.props.history);
    this.setModalShow(false);
  };

  render() {
    const { filtroNome, filtroIndirizzo, errors } = this.state;

    let parcheggiFiltrati = [];
    const { parcheggio } = this.props.listaparcheggi;
    parcheggiFiltrati = filtriParcheggi(parcheggio, filtroNome, "nome");
    parcheggiFiltrati = filtriParcheggi(
      parcheggiFiltrati,
      filtroIndirizzo,
      "indirizzo"
    );

    const VL = (
      <div className="container">
        <div class="center" style={{ textAlign: "left" }}>
          <table class="responsive-table">
            <thead>
              <tr>
                <th>Nome</th>
                <th>Indirizzo</th>
                <th>Numero Civico</th>
                <th>Capienza Auto</th>
                <th>Capienza Moto</th>
                <th>Capienza Biciclette</th>
                <th>Capienza Monopattini</th>
                <th>Elimina</th>
              </tr>
            </thead>
            <tbody>
              {parcheggiFiltrati.map((parcheggio) => (
                <tr key={parcheggio._id}>
                  <td>{parcheggio.nome}</td>
                  <td>{parcheggio.indirizzo}</td>
                  <td>{parcheggio.numero_civico}</td>
                  <td>{parcheggio.capienza_auto}</td>
                  <td>{parcheggio.capienza_moto}</td>
                  <td>{parcheggio.capienza_bici}</td>
                  <td>{parcheggio.capienza_monopattino}</td>
                  <td>
                    {" "}
                    <button
                      class="btn"
                      onClick={() => this.deleteParcheggio(parcheggio._id)}
                    >
                      <i class="material-icons">delete</i>
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );

    return (
      <Container fluid style={{ marginTop: "3rem", marginBottom: "3rem" }}>
        <Modal
          {...this.props}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          centered
          show={this.state.modalShow}
          onHide={() => this.setModalShow(false)}
          backdrop="static"
        >
          <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
              Inserisci i dati del parcheggio
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Container>
              <div className="Form">
                <form noValidate onSubmit={this.onSubmit}>
                  <div className="form-group">
                    <label>Tipo</label>
                    <select
                      id="tipo"
                      type="text"
                      value={this.state.tipo}
                      error={errors.tipo}
                      onChange={this.onChange}
                      className={classnames("form-control", {
                        invalid: errors.tipo,
                      })}
                    >
                      <option value="" disabled selected>
                        Tipo
                      </option>
                      <option value="Auto">Auto</option>
                      <option value="Moto">Moto</option>
                      <option value="Bicicletta">Bicicletta</option>
                      <option value="Monopattino">Monopattino</option>
                    </select>
                    <span className="red-text">{errors.tipo}</span>
                  </div>
                  <div className="form-group">
                    <label htmlFor="modello">Modello</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.modello}
                      id="modello"
                      error={errors.modello}
                      type="text"
                      className={classnames("form-control", {
                        invalid: errors.modello,
                      })}
                    />
                    <span className="red-text">{errors.modello}</span>
                  </div>
                  <div className="form-group">
                    <label htmlFor="cilindrata">Cilindrata</label>
                    <input
                      onChange={this.onChange}
                      value={this.state.cilindrata}
                      error={errors.cilindrata}
                      id="cilindrata"
                      type="number"
                      className={classnames("form-control", {
                        invalid: errors.cilindrata,
                      })}
                    />
                    <span className="red-text">{errors.cilindrata}</span>
                  </div>
                  <div className="form-group">
                    <label>Patente Necessaria</label>
                    <select
                      id="patente_necessaria"
                      type="text"
                      value={this.state.patente_necessaria}
                      onChange={this.onChange}
                      error={errors.patente_necessaria}
                      className={classnames("form-control", {
                        invalid: errors.patente_necessaria,
                      })}
                    >
                      <option value="" disabled selected>
                        Patente
                      </option>
                      <option value="AM">AM</option>
                      <option value="A1">A1</option>
                      <option value="A2">A2</option>
                      <option value="B">B</option>
                      <option value="C">C</option>
                      <option value="NaN">Nessuna</option>
                    </select>
                    <span className="red-text">
                      {errors.patente_necessaria}
                    </span>
                  </div>
                  <button
                    type="submit"
                    value="Aggiunta Parcheggio"
                    className="btn btn-dark btn-lg btn-block bottone"
                  >
                    Inserisci
                  </button>
                </form>
              </div>
            </Container>
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={() => this.setModalShow(false)}>Annulla</Button>
          </Modal.Footer>
        </Modal>
        <div className="Form">
          <Row>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm></Col>
            <Col sm>
              <button
                className="btn btn-dark btn-lg btn-block bottone"
                onClick={() => this.setModalShow(true)}
              >
                + Inserisci Parcheggio
              </button>
            </Col>
          </Row>
          <Row>
            <Col sm>
              <div className="form-group">
                <label htmlFor="tipo">Tipo</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroTipo", event.target.value)
                  }
                  id="tipo"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="modello">Modello</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroModello", event.target.value)
                  }
                  id="modello"
                  type="text"
                />
              </div>
            </Col>
            <Col sm>
              <div className="form-group">
                <label htmlFor="cilindrata">Cilindrata</label>
                <input
                  onChange={(event) =>
                    this.handleChange("filtroCilindrata", event.target.value)
                  }
                  id="cilindrata"
                  type="text"
                />
              </div>
            </Col>
          </Row>
        </div>
        <div className="row">
          {parcheggio.length === 0 ? "Non ci sono parcheggi" : VL}
        </div>
      </Container>
    );
  }
}

AggiornaParcheggi.propTypes = {
  registerParcheggio: PropTypes.func.isRequired,
  Listaparcheggi: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired,
  listaparcheggi: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
  errors: state.errors,
  listaparcheggi: state.listaparcheggi,
});
export default connect(mapStateToProps, {
  registerParcheggio,
  Listaparcheggi,
})(AggiornaParcheggi);
